// src/components/ProductGrid.js - Updated with premium products
import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';
import openaiService from '../services/openaiService';

const ProductGrid = ({ category = 'featured' }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Premium product data
        const premiumProducts = [
          // Modern Cars
          {
            id: 1,
            name: 'Tesla Model S Plaid',
            description: '2024 Electric Sedan, 396mi range, 0-60 in 1.99s',
            originalPrice: 129990,
            category: 'modern-cars',
            image: 'https://images.unsplash.com/photo-1536700503339-1e4b06520771?w=800&h=600&fit=crop',
            brand: 'Tesla',
            specs: ['Electric', 'Ludicrous Mode', 'Autopilot']
          },
          {
            id: 2,
            name: 'Porsche 911 Turbo S',
            description: '2024 Sports Car, 640hp, AWD, PDK Transmission',
            originalPrice: 216100,
            category: 'modern-cars',
            image: 'https://images.unsplash.com/photo-1503376780353-7e6692767b70?w=800&h=600&fit=crop',
            brand: 'Porsche',
            specs: ['3.0L Twin-Turbo', '0-60: 2.6s', 'Top Speed: 205mph']
          },
          
          // Classic Cars
          {
            id: 3,
            name: '1967 Ford Mustang',
            description: 'Fully Restored, Original V8, Manual Transmission',
            originalPrice: 85000,
            category: 'classic-cars',
            image: 'https://images.unsplash.com/photo-1549399542-7e3f8b79c341?w=800&h=600&fit=crop',
            brand: 'Ford',
            specs: ['289ci V8', '4-Speed Manual', 'Concours Condition']
          },
          {
            id: 4,
            name: '1970 Chevrolet Camaro',
            description: 'SS Convertible, Matching Numbers, Restomod',
            originalPrice: 125000,
            category: 'classic-cars',
            image: 'https://images.unsplash.com/photo-1552519507-da3b142c6e3d?w=800&h=600&fit=crop',
            brand: 'Chevrolet',
            specs: ['LS3 V8', '6-Speed Auto', 'Custom Interior']
          },
          
          // Electronics - iPhones
          {
            id: 5,
            name: 'iPhone 16 Pro Max',
            description: '1TB Storage, Titanium Frame, 5x Telephoto Lens',
            originalPrice: 1599,
            category: 'electronics',
            image: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=800&h=600&fit=crop',
            brand: 'Apple',
            specs: ['A18 Pro Chip', '48MP Camera', 'Always-On Display']
          },
          {
            id: 6,
            name: 'Samsung Galaxy S24 Ultra',
            description: '512GB, Titanium Gray, S Pen Included',
            originalPrice: 1399,
            category: 'electronics',
            image: 'https://images.unsplash.com/photo-1610945265064-0e34e5519bbf?w=800&h=600&fit=crop',
            brand: 'Samsung',
            specs: ['Snapdragon 8 Gen 3', '200MP Camera', 'AI Features']
          },
          
          // MacBooks
          {
            id: 7,
            name: 'MacBook Pro 16" M3 Max',
            description: '64GB RAM, 2TB SSD, Space Black',
            originalPrice: 4499,
            category: 'electronics',
            image: 'https://images.unsplash.com/photo-1541807084-5c52b6b3adef?w=800&h=600&fit=crop',
            brand: 'Apple',
            specs: ['40-core GPU', '120Hz XDR Display', '22hr Battery']
          },
          {
            id: 8,
            name: 'ASUS ROG Zephyrus',
            description: 'RTX 4090, AMD Ryzen 9, 32GB RAM, 2TB SSD',
            originalPrice: 3299,
            category: 'electronics',
            image: 'https://images.unsplash.com/photo-1603302576837-37561b2e2302?w=800&h=600&fit=crop',
            brand: 'ASUS',
            specs: ['QHD 240Hz', 'Mini-LED Display', 'Vapor Chamber']
          },
          
          // Luxury Watches
          {
            id: 9,
            name: 'Rolex Submariner',
            description: 'Datejust, Stainless Steel, Black Dial',
            originalPrice: 13500,
            category: 'luxury',
            image: 'https://images.unsplash.com/photo-1523170335258-f5ed11844a49?w=800&h=600&fit=crop',
            brand: 'Rolex',
            specs: ['Automatic', '300m Waterproof', 'Ceramic Bezel']
          },
          {
            id: 10,
            name: 'Audemars Piguet Royal Oak',
            description: 'Automatic, 18K Gold, Blue Dial',
            originalPrice: 85000,
            category: 'luxury',
            image: 'https://images.unsplash.com/photo-1547996160-81f58f6e0e47?w=800&h=600&fit=crop',
            brand: 'Audemars Piguet',
            specs: ['Perpetual Calendar', 'Moonphase', 'Skeleton Back']
          },
          
          // Home Appliances
          {
            id: 11,
            name: 'Dyson Air Purifier',
            description: 'HEPA H13, WiFi Enabled, Voice Control',
            originalPrice: 849,
            category: 'home',
            image: 'https://images.unsplash.com/photo-1581094794329-c8112a89af12?w=800&h=600&fit=crop',
            brand: 'Dyson',
            specs: ['360Â° HEPA', 'Air Multiplier', 'App Control']
          },
          {
            id: 12,
            name: 'Sony 85" 8K TV',
            description: 'Mini-LED, Dolby Vision, Bravia XR Processor',
            originalPrice: 6999,
            category: 'home',
            image: 'https://images.unsplash.com/photo-1593359677879-a4bb92f829d1?w=800&h=600&fit=crop',
            brand: 'Sony',
            specs: ['8K Resolution', '120Hz Refresh', 'Acoustic Surface']
          }
        ];

        // Filter products by category if specified
        let filteredProducts = premiumProducts;
        if (category !== 'featured') {
          filteredProducts = premiumProducts.filter(p => p.category === category);
        }

        // Add AI pricing to each product
        const productsWithPrices = await Promise.all(
          filteredProducts.map(async (product) => {
            try {
              const aiPrice = await openaiService.getFallbackPrice(
                product.name, 
                product.originalPrice
              );
              
              const discountPercent = product.originalPrice > aiPrice 
                ? Math.round(((product.originalPrice - aiPrice) / product.originalPrice) * 100)
                : 0;
              
              return {
                ...product,
                price: aiPrice,
                discount: discountPercent,
                rating: 4.5 + Math.random() * 0.5,
                stock: Math.floor(Math.random() * 50) + 5,
                isNew: Math.random() > 0.7,
                isFeatured: Math.random() > 0.5
              };
            } catch (error) {
              console.error(`Error pricing product ${product.name}:`, error);
              return {
                ...product,
                price: product.originalPrice * 0.9,
                discount: 10,
                rating: 4.0,
                stock: 25,
                isNew: false,
                isFeatured: false
              };
            }
          })
        );

        setProducts(productsWithPrices);
      } catch (error) {
        console.error('Error fetching products:', error);
        setProducts([]);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [category, filter]);

  const filters = [
    { id: 'all', label: 'All Products' },
    { id: 'modern-cars', label: 'Modern Cars' },
    { id: 'classic-cars', label: 'Classic Cars' },
    { id: 'electronics', label: 'Electronics' },
    { id: 'luxury', label: 'Luxury' },
    { id: 'home', label: 'Home & Garden' }
  ];

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-12">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {[...Array(8)].map((_, i) => (
            <div key={i} className="animate-pulse">
              <div className="bg-gradient-to-br from-gray-200 to-gray-300 h-64 rounded-2xl"></div>
              <div className="h-4 bg-gray-200 rounded mt-6"></div>
              <div className="h-3 bg-gray-200 rounded mt-3 w-3/4"></div>
              <div className="h-6 bg-gray-200 rounded mt-4 w-1/2"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Filters */}
      <div className="mb-12">
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-6">
          <div>
            <h1 className="text-4xl font-bold text-gray-900">Premium Collection</h1>
            <p className="text-gray-600 mt-2">AI-powered pricing on luxury items</p>
          </div>
          
          <div className="flex flex-wrap gap-3">
            {filters.map((filterItem) => (
              <button
                key={filterItem.id}
                onClick={() => setFilter(filterItem.id)}
                className={`px-6 py-3 rounded-xl font-medium transition ${
                  filter === filterItem.id
                    ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {filterItem.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
        {products
          .filter(p => filter === 'all' || p.category === filter)
          .map((product) => (
            <ProductCard 
              key={product.id} 
              product={product}
              showAIInfo={true}
              premium={true}
            />
          ))}
      </div>

      {/* Empty State */}
      {products.length === 0 && (
        <div className="text-center py-20">
          <div className="text-5xl mb-6">ðŸš€</div>
          <h3 className="text-2xl font-bold text-gray-900 mb-3">Premium Collection Coming Soon</h3>
          <p className="text-gray-600 max-w-md mx-auto">
            Our AI is curating the best luxury products with smart pricing. Check back soon!
          </p>
        </div>
      )}
    </div>
  );
};

export default ProductGrid;
