// src/components/Header.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { 
  ShoppingCart, 
  User, 
  LogOut, 
  Menu, 
  X, 
  Bell, 
  Settings,
  Search,
  BarChart,
  Globe,
  CreditCard,
  Upload,
  Star,
  TrendingUp,
  Zap,
  ChevronDown,
  ShoppingBag,
  Package,
  DollarSign,
  Activity,
  Shield,
  Clock
} from 'react-feather';
import { useCart } from '../context/CartContext';
import './Header.css';

function Header({ onCartClick, onLoginClick, user, setUser, onApiKeyClick }) {
  const { getCartCount } = useCart();
  const cartCount = getCartCount();
  const navigate = useNavigate();
  const location = useLocation();
  
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const [isCategoryMenuOpen, setIsCategoryMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isLive, setIsLive] = useState(true);
  const [marketStatus, setMarketStatus] = useState({
    global: 'active',
    uk: 'active',
    china: 'active',
    japan: 'active'
  });

  // Simulate live status updates
  useEffect(() => {
    const interval = setInterval(() => {
      setIsLive(prev => !prev); // Toggle for demo
      setMarketStatus(prev => ({
        ...prev,
        global: Math.random() > 0.1 ? 'active' : 'slow',
        uk: Math.random() > 0.15 ? 'active' : 'slow',
        china: Math.random() > 0.2 ? 'active' : 'slow',
        japan: Math.random() > 0.1 ? 'active' : 'slow'
      }));
    }, 30000); // Update every 30 seconds

    return () => clearInterval(interval);
  }, []);

  const handleLogout = () => {
    setUser(null);
    setIsUserMenuOpen(false);
    localStorage.removeItem('user');
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/search?q=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery('');
    }
  };

  const handleGlobalPaymentsClick = () => {
    navigate('/payment-gateway');
    // Scroll to top immediately
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const categories = [
    { name: 'Modern Cars', icon: 'ðŸš—', subcategories: ['Electric Vehicles', 'SUVs', 'Sports Cars', 'Luxury Sedans'] },
    { name: 'Classic Cars', icon: 'ðŸŽï¸', subcategories: ['Vintage', 'Muscle Cars', 'Restored', 'Collectibles'] },
    { name: 'Electronics', icon: 'ðŸ“±', subcategories: ['iPhones', 'Samsung', 'MacBooks', 'Gaming', 'Audio'] },
    { name: 'Auto Parts', icon: 'ðŸ”§', subcategories: ['Engine', 'Suspension', 'Interior', 'Exterior'] },
    { name: 'Fashion', icon: 'ðŸ‘”', subcategories: ['Men', 'Women', 'Accessories', 'Watches'] },
    { name: 'Home & Garden', icon: 'ðŸ¡', subcategories: ['Furniture', 'Appliances', 'Tools', 'Decor'] },
  ];

  const navItems = [
    { 
      path: '/', 
      label: 'Home', 
      icon: null 
    },
    { 
      path: '/products', 
      label: 'Shop All', 
      icon: <ShoppingBag size={16} /> 
    },
    { 
      path: '/market-dashboard', 
      label: 'Market AI', 
      icon: <BarChart size={16} />, 
      badge: 'AI',
      highlight: true 
    },
    { 
      path: '/global-analysis', 
      label: 'Global Analysis', 
      icon: <Globe size={16} />, 
      badge: 'AI',
      highlight: true 
    },
    { 
      path: '/payment-gateway', 
      label: 'Global Payments', 
      icon: <CreditCard size={16} />,
      badge: 'LIVE',
      isLive: isLive,
      highlight: true,
      onClick: handleGlobalPaymentsClick
    },
    { 
      path: '/file-upload', 
      label: 'Upload', 
      icon: <Upload size={16} /> 
    },
    { 
      path: '/seo-analyzer', 
      label: 'SEO Tools', 
      icon: <Search size={16} />,
      badge: 'AI'
    },
  ];

  const userMenuItems = [
    { label: 'My Profile', path: '/profile', icon: <User size={16} /> },
    { label: 'Orders', path: '/orders', icon: <Package size={16} /> },
    { label: 'Payment Methods', path: '/payment-gateway', icon: <CreditCard size={16} /> },
    { label: 'Settings', path: '/settings', icon: <Settings size={16} /> },
    { label: 'Notifications', path: '/notifications', icon: <Bell size={16} /> },
    { type: 'divider' },
    { label: 'Configure API Key', action: onApiKeyClick, icon: <Settings size={16} /> },
    { type: 'divider' },
    { label: 'Logout', action: handleLogout, icon: <LogOut size={16} /> },
  ];

  const marketIndicators = [
    { country: 'Global', status: marketStatus.global, trend: '-0.47%', volume: '$12.85B' },
    { country: 'UK', status: marketStatus.uk, trend: '-0.06%', volume: 'Â£8.85B' },
    { country: 'China', status: marketStatus.china, trend: '-0.36%', volume: 'Â¥102.3B' },
    { country: 'Japan', status: marketStatus.japan, trend: '+0.12%', volume: 'Â¥9.42B' }
  ];

  return (
    <header className="header">
      {/* Top Announcement Bar with Market Status */}
      <div className="announcement-bar">
        <div className="container">
          <div className="announcement-content">
            <div className="announcement-item market-status">
              <span className={`live-indicator ${isLive ? 'live' : 'offline'}`}>
                <Activity size={12} /> {isLive ? 'LIVE' : 'OFFLINE'}
              </span>
              <span className="global-markets">Global Markets:</span>
              
              <div className="market-indicators">
                {marketIndicators.map((market, index) => (
                  <div key={index} className="market-indicator">
                    <span className={`market-dot ${market.status}`}></span>
                    <span className="market-country">{market.country}:</span>
                    <span className={`market-trend ${market.trend.startsWith('+') ? 'positive' : 'negative'}`}>
                      {market.trend}
                    </span>
                    <span className="market-volume">{market.volume}</span>
                  </div>
                ))}
              </div>
              
              <div className="announcement-actions">
                <div className="announcement-item">
                  <span className="announcement-icon">ðŸšš</span>
                  <span>FREE UK Delivery Over Â£50</span>
                </div>
                <div className="announcement-item">
                  <span className="announcement-icon">ðŸ”’</span>
                  <span>Secure Payment</span>
                </div>
                <div className="announcement-item">
                  <span className="announcement-icon">ðŸ”„</span>
                  <span>30-Day Returns</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Header */}
      <div className="main-header">
        <div className="container">
          <div className="header-content">
            {/* Logo & Mobile Menu */}
            <div className="logo-section">
              <button 
                className="mobile-menu-btn"
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                aria-label="Toggle menu"
              >
                {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
              </button>
              
              <Link to="/" className="logo">
                <div className="logo-icon">
                  <Star size={24} />
                </div>
                <div className="logo-text">
                  <span className="logo-primary">UniDigital</span>
                  <span className="logo-tld">.com</span>
                  <span className="logo-tagline">AI-Powered Marketplace</span>
                </div>
                <div className="logo-badges">
                  <span className="ai-badge">
                    <Zap size={10} /> AI
                  </span>
                  <span className="secure-badge">
                    <Shield size={10} /> Secure
                  </span>
                </div>
              </Link>
            </div>

            {/* Category Dropdown */}
            <div className="category-section">
              <button 
                className="category-toggle"
                onClick={() => setIsCategoryMenuOpen(!isCategoryMenuOpen)}
              >
                <span className="category-icon">â˜°</span>
                <span className="category-text">All Categories</span>
                <ChevronDown size={16} />
              </button>
              
              {isCategoryMenuOpen && (
                <div className="category-dropdown">
                  <div className="category-grid">
                    {categories.map((category, index) => (
                      <div key={index} className="category-item">
                        <div className="category-header">
                          <span className="category-emoji">{category.icon}</span>
                          <span className="category-name">{category.name}</span>
                        </div>
                        <div className="subcategories">
                          {category.subcategories.map((sub, subIndex) => (
                            <Link key={subIndex} to={`/category/${sub.toLowerCase()}`} className="subcategory">
                              {sub}
                            </Link>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Search Bar */}
            <div className="search-section">
              <form onSubmit={handleSearch} className="search-form">
                <div className="search-wrapper">
                  <Search size={18} className="search-icon" />
                  <input
                    type="text"
                    placeholder="Search 10M+ premium products, cars, electronics..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-input"
                  />
                  <div className="search-actions">
                    <button type="button" className="voice-search" title="Voice Search">
                      <span>ðŸŽ¤</span>
                    </button>
                    <button type="button" className="ai-search" title="AI Search">
                      <Zap size={14} />
                    </button>
                    <button type="submit" className="search-submit">
                      <Search size={18} />
                    </button>
                  </div>
                </div>
              </form>
            </div>

            {/* User Actions */}
            <div className="actions-section">
              {/* Global Payments Quick Access */}
              <div className="action-item payments-quick">
                <button 
                  className="action-btn payments-btn"
                  onClick={handleGlobalPaymentsClick}
                  aria-label="Global Payments"
                >
                  <div className="payments-indicator">
                    <DollarSign size={20} />
                    <span className={`live-pulse ${isLive ? 'active' : ''}`}></span>
                  </div>
                  <span className="action-label">Payments</span>
                  <span className="live-badge">
                    {isLive ? 'ðŸ”´ LIVE' : 'âš«'}
                  </span>
                </button>
              </div>

              {/* Cart Button */}
              <div className="action-item">
                <button 
                  className="action-btn cart-btn"
                  onClick={onCartClick}
                  aria-label="Shopping cart"
                >
                  <ShoppingCart size={22} />
                  {cartCount > 0 && (
                    <span className="cart-count">{cartCount}</span>
                  )}
                  <span className="action-label">Cart</span>
                </button>
              </div>

              {/* User Account */}
              <div className="action-item user-action">
                {user ? (
                  <>
                    <button 
                      className="action-btn user-btn"
                      onClick={() => setIsUserMenuOpen(!isUserMenuOpen)}
                      aria-label="User menu"
                    >
                      <div className="user-avatar">
                        {user.name?.charAt(0) || user.email?.charAt(0) || <User size={18} />}
                      </div>
                      <span className="user-name">
                        {user.name || user.email?.split('@')[0]}
                      </span>
                      <ChevronDown size={14} />
                    </button>

                    {isUserMenuOpen && (
                      <>
                        <div 
                          className="dropdown-backdrop"
                          onClick={() => setIsUserMenuOpen(false)}
                        />
                        <div className="user-dropdown">
                          <div className="dropdown-header">
                            <div className="dropdown-avatar">
                              {user.name?.charAt(0) || user.email?.charAt(0) || <User size={24} />}
                            </div>
                            <div className="dropdown-info">
                              <div className="dropdown-name">{user.name || 'User'}</div>
                              <div className="dropdown-email">{user.email}</div>
                            </div>
                            <button 
                              className="close-dropdown"
                              onClick={() => setIsUserMenuOpen(false)}
                            >
                              <X size={18} />
                            </button>
                          </div>
                          
                          <div className="dropdown-content">
                            {userMenuItems.map((item, index) => (
                              item.type === 'divider' ? (
                                <div key={index} className="dropdown-divider" />
                              ) : (
                                <button
                                  key={index}
                                  className="dropdown-item"
                                  onClick={() => {
                                    if (item.path) {
                                      navigate(item.path);
                                      setIsUserMenuOpen(false);
                                    } else if (item.action) {
                                      item.action();
                                      setIsUserMenuOpen(false);
                                    }
                                  }}
                                >
                                  <span className="dropdown-icon">{item.icon}</span>
                                  <span className="dropdown-text">{item.label}</span>
                                  {item.label === 'Payment Methods' && (
                                    <span className="dropdown-badge live">LIVE</span>
                                  )}
                                </button>
                              )
                            ))}
                          </div>
                        </div>
                      </>
                    )}
                  </>
                ) : (
                  <button 
                    className="action-btn login-btn"
                    onClick={onLoginClick}
                  >
                    <User size={20} />
                    <span className="action-label">Login</span>
                  </button>
                )}
              </div>

              {/* Notifications */}
              <div className="action-item">
                <button className="action-btn notification-btn" aria-label="Notifications">
                  <Bell size={20} />
                  <span className="notification-dot"></span>
                  <span className="action-label">Alerts</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Bar */}
      <nav className={`main-nav ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
        <div className="container">
          <div className="nav-content">
            <ul className="nav-menu">
              {navItems.map((item, index) => (
                <li key={index} className={`nav-item ${item.highlight ? 'highlight' : ''}`}>
                  <Link 
                    to={item.path}
                    className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
                    onClick={(e) => {
                      if (item.onClick) {
                        e.preventDefault();
                        item.onClick();
                      }
                      setIsMobileMenuOpen(false);
                    }}
                  >
                    {item.icon && <span className="nav-icon">{item.icon}</span>}
                    <span className="nav-text">{item.label}</span>
                    {item.badge && (
                      <span className={`nav-badge ${item.isLive ? 'live' : ''}`}>
                        {item.isLive && <span className="pulse"></span>}
                        <Zap size={10} /> {item.badge}
                      </span>
                    )}
                  </Link>
                </li>
              ))}
            </ul>

            {/* Live Trends & Payments Status */}
            <div className="trends-bar">
              <div className="trends-label">
                <TrendingUp size={14} />
                <span>Live Markets:</span>
              </div>
              <div className="trends-list">
                <span className="trend-item trending">
                  <span className="trend-icon">ðŸŒ</span>
                  Global: <strong>98.7% AI Accuracy</strong>
                </span>
                <a 
                  href="/payment-gateway" 
                  className="trend-item flash payments-highlight"
                  onClick={(e) => {
                    e.preventDefault();
                    handleGlobalPaymentsClick();
                  }}
                >
                  <span className="trend-icon">âš¡</span>
                  <strong>Global Payments LIVE</strong>
                  <span className="live-indicator-small">
                    <Activity size={10} /> LIVE
                  </span>
                </a>
                <a href="/market-dashboard" className="trend-item summer">
                  <span className="trend-icon">ðŸ“Š</span>
                  Real-time Analysis
                </a>
                <span className="trend-item new">
                  <span className="trend-icon">ðŸŽ¯</span>
                  AI-Priced Items
                </span>
              </div>
              
              {/* Quick Actions */}
              <div className="nav-quick-actions">
                <button 
                  className="quick-action-btn"
                  onClick={() => navigate('/market-dashboard')}
                >
                  <BarChart size={14} />
                  <span>Market AI</span>
                </button>
                <button 
                  className="quick-action-btn payments-action"
                  onClick={handleGlobalPaymentsClick}
                >
                  <CreditCard size={14} />
                  <span>Payments</span>
                  <span className="live-dot"></span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </nav>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-overlay"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}
    </header>
  );
}

export default Header;
