import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import './ModernHeader.css';

const ModernHeader = () => {
  const { cartCount } = useCart();
  const navigate = useNavigate();
  const [liveData, setLiveData] = useState({
    carIndex: '+2.2%',
    electronics: '+1.5%',
    evDemand: '+20%',
    time: new Date().toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit' })
  });

  const [userLocation, setUserLocation] = useState('GB');
  const [currency, setCurrency] = useState('GBP');

  // Update live data every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setLiveData(prev => ({
        ...prev,
        time: new Date().toLocaleTimeString('en-GB', { hour: '2-digit', minute: '2-digit' })
      }));
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  // Detect user location
  useEffect(() => {
    const timezone = Intl.DateTimeFormat().resolvedOptions().timeZone;
    if (timezone.includes('America')) setUserLocation('US');
    else if (timezone.includes('Europe')) setUserLocation('EU');
  }, []);

  const countries = {
    'GB': 'üá¨üáß UK',
    'US': 'üá∫üá∏ US',
    'EU': 'üá™üá∫ EU',
    'AU': 'üá¶üá∫ AU',
    'AE': 'üá¶üá™ UAE'
  };

  const currencies = {
    'GBP': '¬£ GBP',
    'USD': '$ USD',
    'EUR': '‚Ç¨ EUR',
    'AED': 'ÿØ.ÿ• AED'
  };

  return (
    <header className="modern-header">
      {/* Live Status Bar */}
      <div className="live-status-bar">
        <div className="live-indicator">
          <span className="pulse-dot"></span>
          LIVE MARKET DATA
        </div>
        <div className="live-stats">
          <span>Car Index: <strong className="up">{liveData.carIndex}</strong></span>
          <span>Electronics: <strong className="up">{liveData.electronics}</strong></span>
          <span>EV Demand: <strong className="up">{liveData.evDemand}</strong></span>
          <span>Last Update: <strong>{liveData.time}</strong></span>
        </div>
      </div>

      {/* Main Header */}
      <div className="header-main">
        {/* Logo Section */}
        <div className="logo-section" onClick={() => navigate('/')}>
          <div className="logo">UD</div>
          <div className="brand">
            <h1>UniDigital</h1>
            <p className="tagline">Global Marketplace</p>
          </div>
        </div>

        {/* Search Section */}
        <div className="search-section">
          <div className="search-container">
            <input 
              type="text" 
              placeholder="Search 10M+ products, categories, or AI insights..." 
              className="search-input"
            />
            <button className="ai-search-btn">
              <span className="ai-icon">ü§ñ</span> AI Search
            </button>
          </div>
          <div className="quick-trends">
            <span>üî• AI-Priced Deals</span>
            <span>üìà Trending Now</span>
            <span>‚ö° Flash Deals</span>
            <span>‚òÄÔ∏è Summer Sale</span>
          </div>
        </div>

        {/* User Actions */}
        <div className="user-actions">
          {/* Location Selector */}
          <div className="location-selector">
            <select 
              value={userLocation}
              onChange={(e) => setUserLocation(e.target.value)}
              className="location-dropdown"
            >
              {Object.entries(countries).map(([code, name]) => (
                <option key={code} value={code}>{name}</option>
              ))}
            </select>
          </div>

          {/* Currency Selector */}
          <div className="currency-selector">
            <select 
              value={currency}
              onChange={(e) => setCurrency(e.target.value)}
              className="currency-dropdown"
            >
              {Object.entries(currencies).map(([code, symbol]) => (
                <option key={code} value={code}>{symbol}</option>
              ))}
            </select>
          </div>

          {/* Sign In */}
          <button className="auth-btn" onClick={() => navigate('/auth')}>
            üë§ Sign In
          </button>

          {/* Cart */}
          <Link to="/cart" className="cart-btn">
            üõí Cart
            {cartCount > 0 && <span className="cart-count">{cartCount}</span>}
          </Link>
        </div>
      </div>

      {/* Navigation */}
      <nav className="main-nav">
        <Link to="/">üè† Home</Link>
        <Link to="/category/cars">üöó Cars</Link>
        <Link to="/category/electronics">üì± Electronics</Link>
        <Link to="/category/auto-parts">üîß Auto Parts</Link>
        <Link to="/category/smartphones">üì± Smartphones</Link>
        <Link to="/category/laptops">üíª Laptops</Link>
        <Link to="/category/gaming">üéÆ Gaming</Link>
        <Link to="/category/luxury-cars">üíé Luxury Cars</Link>
        <Link to="/category/electric-cars">‚ö° Electric Cars</Link>
        <Link to="/market-analysis">üìä Market Analysis</Link>
        <Link to="/payment">üí≥ Global Payments</Link>
      </nav>
    </header>
  );
};

export default ModernHeader;
