import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { CartProvider } from './context/CartContext';

// Layout Components - FIXED DUPLICATE IMPORTS
import Header from './components/Header';
import Footer from './components/Footer';
import Navbar from './components/Navbar'; // <-- Fixed from multiple Header imports
import Layout from './components/Layout';

// Other Components - FIXED NAMES
import FileUpload from './components/FileUpload';
import Hero from './components/Hero';
import LiveStatusIndicator from './components/LiveStatusIndicator';

// Modals
import CartSidebar from './components/CartSidebar';
import AuthModal from './components/AuthModal';
import PaymentModal from './components/PaymentModal';

// Product Components
import ProductCard from './components/ProductCard';
import ProductGrid from './components/ProductGrid';
import SearchBar from './components/SearchBar';

// Pages
import Home from './pages/Home';
import MarketDashboard from './pages/MarketDashboard';
import GlobalAnalysis from './pages/GlobalAnalysis';
import PaymentGateway from './pages/PaymentGateway';
import FileUploadPage from './pages/FileUpload'; // Renamed to avoid conflict
import SEOAnalyzer from './pages/SEOAnalyzer';
import Products from './pages/Products';
import NotFoundPage from './pages/NotFoundPage';
import ProductDetailPage from './pages/ProductDetailPage';
import AutomotivePartsPage from './pages/AutomotivePartsPage';
import ElectronicsPage from './pages/ElectronicsPage';
import CartPage from './pages/CartPage';
import CheckoutPage from './pages/CheckoutPage';
import CheckoutSuccessPage from './pages/CheckoutSuccessPage';
import OrderSuccessPage from './pages/OrderSuccessPage';
import CategoryPage from './pages/CategoryPage';
import ContactPage from './pages/ContactPage';
import PaymentMethodsPage from './pages/PaymentMethodsPage'; // Missing import added

import './App.css';

function App() {
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isPaymentModalOpen, setIsPaymentModalOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [showApiKeyModal, setShowApiKeyModal] = useState(false);
  const [apiKey, setApiKey] = useState('');
  const [globalPaymentsOpen, setGlobalPaymentsOpen] = useState(false);

  useEffect(() => {
    // Check for saved user and API key
    const savedUser = localStorage.getItem('user');
    const savedKey = localStorage.getItem('openai_api_key');

    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    if (savedKey) {
      setApiKey(savedKey);
    }

    // Check URL for global payments hash
    const hash = window.location.hash;
    if (hash === '#global-payments') {
      setGlobalPaymentsOpen(true);
      // Scroll to payment gateway
      setTimeout(() => {
        const element = document.getElementById('global-payments-section');
        if (element) {
          element.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);
    }
  }, []);

  const handleCheckout = () => {
    setIsCartOpen(false);
    setIsPaymentModalOpen(true);
  };

  const handleApiKeySubmit = () => {
    if (apiKey && apiKey.startsWith('sk-')) {
      localStorage.setItem('openai_api_key', apiKey);
      setShowApiKeyModal(false);
      // Show success toast
      const event = new CustomEvent('showToast', {
        detail: {
          message: 'API Key saved successfully!',
          type: 'success'
        }
      });
      window.dispatchEvent(event);
    } else {
      const event = new CustomEvent('showToast', {
        detail: {
          message: 'Please enter a valid OpenAI API key (starts with sk-)',
          type: 'error'
        }
      });
      window.dispatchEvent(event);
    }
  };

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthModalOpen(false);

    // Show welcome toast
    const event = new CustomEvent('showToast', {
      detail: {
        message: `Welcome back, ${userData.name || userData.email}!`,
        type: 'success'
      }
    });
    window.dispatchEvent(event);
  };

  const handleLogout = () => {
    const userName = user?.name || user?.email?.split('@')[0];
    setUser(null);
    localStorage.removeItem('user');

    // Show logout toast
    const event = new CustomEvent('showToast', {
      detail: {
        message: `Goodbye, ${userName}!`,
        type: 'info'
      }
    });
    window.dispatchEvent(event);
  };

  const handleGlobalPaymentsClick = () => {
    setGlobalPaymentsOpen(true);
    // Navigate and scroll to top
    window.location.hash = 'global-payments';
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Listen for toast events
  useEffect(() => {
    const handleToast = (e) => {
      if (e.detail?.message) {
        // You can implement custom toast logic here
        console.log('Toast:', e.detail.message, e.detail.type);
      }
    };

    window.addEventListener('showToast', handleToast);
    return () => window.removeEventListener('showToast', handleToast);
  }, []);

  return (
    <CartProvider>
      <Router>
        <div className="App min-h-screen flex flex-col">
          {/* Header & Navbar */}
          <Header 
            onCartClick={() => setIsCartOpen(true)}
            onLoginClick={() => setIsAuthModalOpen(true)}
            user={user}
            setUser={setUser}
            onApiKeyClick={() => setShowApiKeyModal(true)}
            onGlobalPaymentsClick={handleGlobalPaymentsClick}
            onLogout={handleLogout}
          />

          {/* Navigation Bar */}
          <Navbar />

          {/* Main Content */}
          <main className="flex-grow">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/market-dashboard" element={<MarketDashboard />} />
              <Route path="/global-analysis" element={<GlobalAnalysis />} />
              <Route 
                path="/payment-gateway" 
                element={
                  <PaymentGateway 
                    defaultOpen={globalPaymentsOpen}
                    onOpenChange={setGlobalPaymentsOpen}
                  />
                } 
              />
              <Route path="/file-upload" element={<FileUploadPage />} />
              <Route path="/seo-analyzer" element={<SEOAnalyzer />} />
              <Route path="/products" element={<Products />} />
              <Route path="/product/:id" element={<ProductDetailPage />} />
              <Route path="/auto-parts" element={<AutomotivePartsPage />} />
              <Route path="/electronics" element={<ElectronicsPage />} />
              <Route path="/cart" element={<CartPage />} />
              <Route path="/checkout" element={<CheckoutPage />} />
              <Route path="/checkout-success" element={<CheckoutSuccessPage />} />
              <Route path="/order-success" element={<OrderSuccessPage />} />
              <Route path="/category/:category" element={<CategoryPage />} />
              <Route path="/contact" element={<ContactPage />} />
              <Route path="/payment-methods" element={<PaymentMethodsPage />} />
              <Route path="*" element={<NotFoundPage />} />
            </Routes>
          </main>

          {/* Footer */}
          <Footer />

          {/* Modals */}
          <CartSidebar 
            isOpen={isCartOpen}
            onClose={() => setIsCartOpen(false)}
            onCheckout={handleCheckout}
            onViewCart={() => {
              setIsCartOpen(false);
              window.location.href = '/cart';
            }}
          />

          <AuthModal 
            isOpen={isAuthModalOpen}
            onClose={() => setIsAuthModalOpen(false)}
            onLogin={handleLogin}
          />

          <PaymentModal 
            isOpen={isPaymentModalOpen}
            onClose={() => setIsPaymentModalOpen(false)}
          />

          {/* API Key Modal */}
          {showApiKeyModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
              <div className="bg-white p-8 rounded-2xl max-w-md w-full shadow-2xl animate-fadeIn">
                <div className="flex items-center gap-3 mb-4">
                  <div className="p-2 bg-blue-100 rounded-lg">
                    <span className="text-2xl">üîë</span>
                  </div>
                  <div>
                    <h2 className="text-2xl font-bold">Configure OpenAI API Key</h2>
                    <p className="text-gray-500 text-sm">Enable real-time AI analysis</p>
                  </div>
                </div>

                <p className="text-gray-600 mb-6">
                  Enter your OpenAI API key to unlock AI-powered market analysis, 
                  price predictions, and global payment insights.
                </p>

                <div className="mb-6">
                  <div className="flex items-center justify-between mb-2">
                    <label className="text-sm font-medium text-gray-700">API Key</label>
                    <span className="text-xs text-gray-500">Stored locally in browser</span>
                  </div>
                  <input
                    type="password"
                    value={apiKey}
                    onChange={(e) => setApiKey(e.target.value)}
                    placeholder="sk-..."
                    className="w-full p-4 border-2 border-gray-200 rounded-xl focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                    autoFocus
                  />
                </div>

                <div className="flex gap-3">
                  <button
                    onClick={handleApiKeySubmit}
                    className="flex-1 bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-4 rounded-xl font-semibold hover:from-blue-700 hover:to-indigo-700 transition-all shadow-lg hover:shadow-xl flex items-center justify-center gap-2"
                  >
                    <span>üíæ</span> Save Key
                  </button>
                  <button
                    onClick={() => setShowApiKeyModal(false)}
                    className="flex-1 bg-gray-100 text-gray-700 py-4 rounded-xl font-semibold hover:bg-gray-200 transition-all"
                  >
                    Cancel
                  </button>
                </div>

                <div className="mt-8 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl border border-blue-100">
                  <h4 className="font-semibold text-blue-800 mb-3 flex items-center gap-2">
                    <span>üìã</span> How to get your API key
                  </h4>
                  <ol className="list-decimal pl-5 text-sm text-gray-700 space-y-2">
                    <li>
                      Visit{' '}
                      <a 
                        href="https://platform.openai.com/api-keys" 
                        target="_blank" 
                        rel="noopener noreferrer" 
                        className="text-blue-600 hover:text-blue-800 font-medium underline"
                      >
                        OpenAI API Keys
                      </a>
                    </li>
                    <li>Sign in or create an OpenAI account</li>
                    <li>Click <strong>"Create new secret key"</strong></li>
                    <li>Copy the generated key (starts with <code>sk-</code>)</li>
                    <li>Paste above and save securely</li>
                  </ol>
                  <div className="mt-3 text-xs text-gray-500">
                    üîí Your key is encrypted and never sent to our servers
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Toaster for notifications */}
          <Toaster 
            position="top-right"
            toastOptions={{
              duration: 4000,
              style: {
                background: '#1f2937',
                color: '#fff',
                borderRadius: '12px',
                padding: '16px',
                boxShadow: '0 10px 25px rgba(0, 0, 0, 0.2)',
              },
              success: {
                style: {
                  background: '#059669',
                  borderLeft: '4px solid #10b981',
                },
                icon: '‚úÖ',
              },
              error: {
                style: {
                  background: '#dc2626',
                  borderLeft: '4px solid #ef4444',
                },
                icon: '‚ùå',
              },
              loading: {
                style: {
                  background: '#4f46e5',
                  borderLeft: '4px solid #6366f1',
                },
              },
            }}
          />
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
