// src/components/ProductCard.js - CORRECTED VERSION
import React from 'react';
import { ShoppingCart, Eye, Star, TrendingUp, Shield } from 'lucide-react';
import { useCart } from '../context/CartContext';

const ProductCard = ({ product, showAIInfo = false, premium = false }) => {
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    addToCart(product);
  };

  const formatPrice = (price) => {
    if (price >= 1000) {
      return new Intl.NumberFormat('en-GB', {
        style: 'currency',
        currency: 'GBP',
        minimumFractionDigits: 0,
        maximumFractionDigits: 0
      }).format(price);
    }
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP'
    }).format(price);
  };

  const getCategoryColor = (category) => {
    const colors = {
      'modern-cars': 'from-blue-500 to-cyan-500',
      'classic-cars': 'from-amber-500 to-orange-500',
      'electronics': 'from-purple-500 to-pink-500',
      'luxury': 'from-rose-500 to-red-500',
      'home': 'from-emerald-500 to-green-500'
    };
    return colors[category] || 'from-gray-500 to-gray-600';
  };

  return (
    <div className="group relative">
      {/* Premium Badge */}
      {premium && (
        <div className="absolute top-4 left-4 z-10">
          <span className="bg-gradient-to-r from-amber-500 to-orange-500 text-white text-xs font-bold px-3 py-1 rounded-full">
            PREMIUM
          </span>
        </div>
      )}

      {/* AI Pricing Badge */}
      {showAIInfo && product.discount > 0 && (
        <div className="absolute top-4 right-4 z-10">
          <div className="bg-gradient-to-r from-green-500 to-emerald-600 text-white text-sm font-bold px-3 py-1 rounded-full flex items-center gap-1">
            <TrendingUp size={12} />
            AI PRICING
          </div>
        </div>
      )}

      <div className="bg-white rounded-2xl shadow-xl hover:shadow-2xl transition-all duration-300 overflow-hidden border border-gray-100 group-hover:border-blue-200">
        {/* Image Container */}
        <div className="relative overflow-hidden bg-gradient-to-br from-gray-50 to-gray-100">
          <img
            src={product.image}
            alt={product.name}
            className="w-full h-64 object-cover transition-transform duration-500 group-hover:scale-105"
          />
          
          {/* Category Tag */}
          <div className={`absolute bottom-4 left-4 bg-gradient-to-r ${getCategoryColor(product.category)} text-white text-xs font-semibold px-3 py-1 rounded-full`}>
            {product.brand || product.category.replace('-', ' ').toUpperCase()}
          </div>

          {/* Quick Actions */}
          <div className="absolute top-4 left-4 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
            <button className="bg-white/90 backdrop-blur-sm p-2 rounded-full shadow-lg hover:bg-white transition">
              <Eye size={18} className="text-gray-700" />
            </button>
          </div>
        </div>

        {/* Product Info */}
        <div className="p-6">
          {/* Brand & Rating */}
          <div className="flex justify-between items-start mb-3">
            <div>
              {product.brand && (
                <span className="text-xs font-semibold text-gray-500 uppercase tracking-wide">
                  {product.brand}
                </span>
              )}
            </div>
            <div className="flex items-center gap-1">
              <Star size={14} className="text-yellow-400 fill-current" />
              <span className="text-sm font-bold text-gray-900">{product.rating.toFixed(1)}</span>
            </div>
          </div>

          {/* Product Name */}
          <h3 className="text-lg font-bold text-gray-900 mb-2 line-clamp-1">
            {product.name}
          </h3>

          {/* Description */}
          <p className="text-sm text-gray-600 mb-4 line-clamp-2">
            {product.description}
          </p>

          {/* Specs */}
          {product.specs && (
            <div className="flex flex-wrap gap-2 mb-4">
              {product.specs.slice(0, 2).map((spec, index) => (
                <span key={index} className="text-xs bg-blue-50 text-blue-700 px-2 py-1 rounded">
                  {spec}
                </span>
              ))}
            </div>
          )}

          {/* Pricing */}
          <div className="mb-6">
            <div className="flex items-center gap-3">
              <span className="text-2xl font-bold text-gray-900">
                {formatPrice(product.price)}
              </span>
              
              {product.discount > 0 && (
                <>
                  <span className="text-sm line-through text-gray-400">
                    {formatPrice(product.originalPrice)}
                  </span>
                  <span className="text-sm font-bold bg-gradient-to-r from-green-500 to-emerald-600 text-white px-2 py-1 rounded">
                    Save {product.discount}%
                  </span>
                </>
              )}
            </div>

            {/* AI Pricing Info */}
            {showAIInfo && (
              <div className="mt-2 flex items-center gap-2 text-xs text-gray-500">
                <TrendingUp size={12} />
                <span>AI Price Match Guarantee</span>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="flex gap-3">
            <button
              onClick={handleAddToCart}
              className="flex-1 bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 rounded-xl font-semibold hover:from-blue-700 hover:to-purple-700 transition-all duration-300 flex items-center justify-center gap-2 shadow-lg hover:shadow-xl"
            >
              <ShoppingCart size={18} />
              Add to Cart
            </button>
            
            <button className="px-4 border-2 border-gray-300 text-gray-700 rounded-xl font-semibold hover:border-blue-500 hover:bg-blue-50 transition">
              View
            </button>
          </div>

          {/* Stock Info */}
          <div className="mt-4 pt-4 border-t border-gray-100">
            <div className="flex justify-between items-center text-sm">
              <div className="flex items-center gap-2">
                <div className={`h-2 w-2 rounded-full ${product.stock > 10 ? 'bg-green-500' : 'bg-red-500'}`}></div>
                <span className={product.stock > 10 ? 'text-green-600' : 'text-red-600'}>
                  {product.stock > 10 ? 'In Stock' : 'Low Stock'}
                </span>
              </div>
              <div className="flex items-center gap-2 text-gray-500">
                <Shield size={12} />
                <span>1 Year Warranty</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
