import React, { useState } from 'react';

const FileUpload = () => {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);

  const handleFileSelect = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles([...files, ...selectedFiles]);
  };

  const handleUpload = async () => {
    if (files.length === 0) return;
    
    setUploading(true);
    const formData = new FormData();
    files.forEach(file => {
      formData.append('files', file);
    });

    try {
      // Simulate upload
      await new Promise(resolve => setTimeout(resolve, 2000));
      alert(`${files.length} files uploaded successfully!`);
      setFiles([]);
    } catch (error) {
      alert('Upload failed. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  const removeFile = (index) => {
    setFiles(files.filter((_, i) => i !== index));
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFiles = Array.from(e.dataTransfer.files);
    setFiles([...files, ...droppedFiles]);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-2">üìÅ File Upload Center</h1>
      <p className="text-gray-600 mb-8">Upload product images, CSVs, documents, and more</p>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Upload Area */}
        <div className="lg:col-span-2">
          <div 
            className="border-4 border-dashed border-gray-300 rounded-2xl p-12 text-center mb-6"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
          >
            <div className="text-6xl mb-4">üìÅ</div>
            <h2 className="text-2xl font-bold mb-2">Drag & Drop Files</h2>
            <p className="text-gray-600 mb-6">or click to browse files</p>
            
            <input
              type="file"
              id="file-input"
              multiple
              onChange={handleFileSelect}
              className="hidden"
            />
            
            <label
              htmlFor="file-input"
              className="inline-block bg-gradient-to-r from-blue-500 to-purple-500 text-white px-8 py-3 rounded-lg font-semibold cursor-pointer hover:shadow-lg"
            >
              Browse Files
            </label>
            
            <p className="text-sm text-gray-500 mt-4">
              Supports: Images (PNG, JPG, GIF), PDF, CSV, Excel, Word (Max 50MB each)
            </p>
          </div>

          {/* File List */}
          {files.length > 0 && (
            <div className="bg-white rounded-xl shadow-lg p-6">
              <h3 className="text-xl font-bold mb-4">Selected Files ({files.length})</h3>
              
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {files.map((file, index) => (
                  <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                    <div className="flex items-center">
                      <div className="text-2xl mr-4">
                        {file.type.startsWith('image/') ? 'üñºÔ∏è' : 'üìÑ'}
                      </div>
                      <div>
                        <div className="font-medium">{file.name}</div>
                        <div className="text-sm text-gray-500">
                          {(file.size / 1024 / 1024).toFixed(2)} MB
                        </div>
                      </div>
                    </div>
                    
                    <button
                      onClick={() => removeFile(index)}
                      className="text-red-500 hover:text-red-700"
                    >
                      ‚úï
                    </button>
                  </div>
                ))}
              </div>

              <div className="flex gap-4 mt-6">
                <button
                  onClick={() => setFiles([])}
                  className="px-6 py-3 border border-gray-300 rounded-lg hover:bg-gray-50"
                >
                  Clear All
                </button>
                <button
                  onClick={handleUpload}
                  disabled={uploading}
                  className="flex-1 bg-gradient-to-r from-green-500 to-emerald-600 text-white py-3 rounded-lg font-semibold hover:shadow-lg disabled:opacity-50"
                >
                  {uploading ? 'Uploading...' : `Upload ${files.length} Files`}
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Upload Info Sidebar */}
        <div className="space-y-6">
          <div className="bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-xl p-6">
            <h3 className="text-xl font-semibold mb-2">üì§ Upload Stats</h3>
            <div className="text-4xl font-bold mb-2">99.9%</div>
            <p className="opacity-90">Success Rate</p>
          </div>

          <div className="bg-white rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold mb-4">üí° Quick Tips</h3>
            <ul className="space-y-3">
              <li className="flex items-start">
                <span className="text-green-500 mr-2">‚úì</span>
                <span>Use high-quality images for better conversions</span>
              </li>
              <li className="flex items-start">
                <span className="text-green-500 mr-2">‚úì</span>
                <span>CSV files should include price, quantity, SKU</span>
              </li>
              <li className="flex items-start">
                <span className="text-green-500 mr-2">‚úì</span>
                <span>Optimize images before upload (max 2MB)</span>
              </li>
              <li className="flex items-start">
                <span className="text-green-500 mr-2">‚úì</span>
                <span>Use descriptive filenames for SEO</span>
              </li>
            </ul>
          </div>

          <div className="bg-white rounded-xl shadow-lg p-6">
            <h3 className="text-lg font-semibold mb-4">‚ö° Batch Actions</h3>
            <div className="space-y-3">
              <button className="w-full p-3 bg-blue-50 text-blue-600 rounded-lg hover:bg-blue-100">
                üì∑ Bulk Image Optimizer
              </button>
              <button className="w-full p-3 bg-green-50 text-green-600 rounded-lg hover:bg-green-100">
                üìä CSV to Product Import
              </button>
              <button className="w-full p-3 bg-purple-50 text-purple-600 rounded-lg hover:bg-purple-100">
                üè∑Ô∏è Auto Tag Generator
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
