import React, { useState } from 'react';
import { 
  Globe, CreditCard, Building, Wallet, 
  CheckCircle, Shield, Zap, Lock, Award, TrendingUp 
} from 'lucide-react';

const GlobalPaymentsPage = () => {
  const [activeRegion, setActiveRegion] = useState('all');

  const paymentMethods = [
    {
      region: 'africa',
      name: 'African Payments',
      icon: <Globe className="h-8 w-8" />,
      color: 'from-green-500 to-emerald-600',
      methods: [
        { name: 'M-Pesa', country: 'Kenya/Tanzania', fee: '1.5%', speed: 'Instant' },
        { name: 'Flutterwave', country: 'Nigeria/Ghana', fee: '2.0%', speed: '1-2 hours' },
        { name: 'Paystack', country: 'Nigeria', fee: '1.5%', speed: 'Instant' },
        { name: 'Orange Money', country: 'West Africa', fee: '2.0%', speed: 'Instant' },
        { name: 'Airtel Money', country: 'East Africa', fee: '1.8%', speed: 'Instant' }
      ]
    },
    {
      region: 'eu',
      name: 'European Union',
      icon: <Building className="h-8 w-8" />,
      color: 'from-blue-500 to-indigo-600',
      methods: [
        { name: 'SEPA', country: 'EU Zone', fee: '0.5%', speed: '1-2 days' },
        { name: 'iDEAL', country: 'Netherlands', fee: '1.0%', speed: 'Instant' },
        { name: 'Giropay', country: 'Germany', fee: '1.2%', speed: 'Instant' },
        { name: 'SOFORT', country: 'DACH Region', fee: '1.5%', speed: 'Instant' },
        { name: 'Bancontact', country: 'Belgium', fee: '1.0%', speed: 'Instant' }
      ]
    },
    {
      region: 'us',
      name: 'United States',
      icon: <CreditCard className="h-8 w-8" />,
      color: 'from-red-500 to-pink-600',
      methods: [
        { name: 'PayPal', country: 'Global', fee: '2.9%', speed: 'Instant' },
        { name: 'Venmo', country: 'USA', fee: '1.9%', speed: 'Instant' },
        { name: 'Zelle', country: 'USA', fee: '0%', speed: 'Instant' },
        { name: 'Apple Pay', country: 'USA', fee: '1.5%', speed: 'Instant' },
        { name: 'Google Pay', country: 'Global', fee: '1.9%', speed: 'Instant' }
      ]
    },
    {
      region: 'asia',
      name: 'Asia Pacific',
      icon: <Globe className="h-8 w-8" />,
      color: 'from-yellow-500 to-orange-600',
      methods: [
        { name: 'Alipay', country: 'China', fee: '1.5%', speed: 'Instant' },
        { name: 'WeChat Pay', country: 'China', fee: '1.6%', speed: 'Instant' },
        { name: 'PayTM', country: 'India', fee: '2.0%', speed: 'Instant' },
        { name: 'GrabPay', country: 'SE Asia', fee: '1.8%', speed: 'Instant' },
        { name: 'Line Pay', country: 'Japan', fee: '2.0%', speed: 'Instant' }
      ]
    },
    {
      region: 'digital',
      name: 'Digital Wallets',
      icon: <Wallet className="h-8 w-8" />,
      color: 'from-purple-500 to-pink-600',
      methods: [
        { name: 'Apple Pay', icon: 'üçé', fee: '1.5%', coverage: 'Global' },
        { name: 'Google Pay', icon: 'ü§ñ', fee: '1.9%', coverage: 'Global' },
        { name: 'Samsung Pay', icon: 'üì±', fee: '1.7%', coverage: 'Global' },
        { name: 'Amazon Pay', icon: 'üì¶', fee: '2.9%', coverage: 'Global' },
        { name: 'Revolut', icon: 'üí≥', fee: '1.0%', coverage: 'Global' }
      ]
    },
    {
      region: 'crypto',
      name: 'Cryptocurrency',
      icon: <TrendingUp className="h-8 w-8" />,
      color: 'from-gray-700 to-black',
      methods: [
        { name: 'Bitcoin (BTC)', symbol: '‚Çø', fee: '0.8%', speed: '10-30 min' },
        { name: 'Ethereum (ETH)', symbol: 'Œû', fee: '1.2%', speed: '2-5 min' },
        { name: 'USDT', symbol: '‚ÇÆ', fee: '0.5%', speed: 'Instant' },
        { name: 'BNB', symbol: 'B', fee: '0.1%', speed: 'Instant' },
        { name: 'Cardano (ADA)', symbol: 'A', fee: '0.5%', speed: '1-5 min' }
      ]
    }
  ];

  const features = [
    { icon: <Shield />, title: 'Bank-Level Security', desc: '256-bit encryption & fraud protection' },
    { icon: <Zap />, title: 'Instant Processing', desc: 'Real-time payment confirmation' },
    { icon: <Globe />, title: '150+ Currencies', desc: 'Support for global transactions' },
    { icon: <Lock />, title: 'PCI DSS Compliant', desc: 'Highest security standards' },
    { icon: <Award />, title: '24/7 Support', desc: 'Round-the-clock customer service' },
    { icon: <CheckCircle />, title: '99.9% Uptime', desc: 'Reliable payment infrastructure' }
  ];

  const filteredPayments = activeRegion === 'all' 
    ? paymentMethods 
    : paymentMethods.filter(p => p.region === activeRegion);

  // Add this for better UX - smooth scroll when region changes
  const handleRegionChange = (region) => {
    setActiveRegion(region);
    // Smooth scroll to payment methods section
    setTimeout(() => {
      const element = document.getElementById('payment-methods-section');
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      }
    }, 100);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-blue-900 via-purple-900 to-indigo-900 text-white py-20">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <h1 className="text-5xl font-bold mb-6">Global Payment Solutions</h1>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto mb-10">
            Accept payments from anywhere in the world with our comprehensive suite of payment methods. 
            From African mobile money to European bank transfers and digital wallets.
          </p>
          <div className="flex flex-wrap justify-center gap-4 mb-12">
            <button 
              onClick={() => window.scrollTo({ top: 700, behavior: 'smooth' })}
              className="px-6 py-3 bg-white text-blue-900 font-bold rounded-full hover:bg-gray-100 transition-all duration-300"
            >
              Get Started Free
            </button>
            <button className="px-6 py-3 border-2 border-white text-white rounded-full hover:bg-white/10 transition-all duration-300">
              Schedule Demo
            </button>
