        <h2 className="text-3xl font-bold text-center mb-12">Enterprise-Grade Payment Infrastructure</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-16">
          {features.map((feature, idx) => (
            <div key={idx} className="bg-white p-8 rounded-2xl shadow-lg border border-gray-200 hover:shadow-xl transition-all duration-300">
              <div className="text-blue-600 mb-4">{feature.icon}</div>
              <h3 className="text-xl font-bold mb-2">{feature.title}</h3>
              <p className="text-gray-600">{feature.desc}</p>
            </div>
          ))}
        </div>

        {/* Region Filter */}
        <div className="flex flex-wrap gap-3 justify-center mb-12">
          <button 
            onClick={() => handleRegionChange('all')}
            className={`px-6 py-3 rounded-full font-medium transition-all duration-300 ${activeRegion === 'all' ? 'bg-blue-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
          >
            All Regions
          </button>
          {paymentMethods.map(region => (
            <button
              key={region.region}
              onClick={() => handleRegionChange(region.region)}
              className={`px-6 py-3 rounded-full font-medium transition-all duration-300 ${activeRegion === region.region ? 'bg-blue-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
            >
              {region.name}
            </button>
          ))}
        </div>

        {/* Payment Methods Grid */}
        <div id="payment-methods-section" className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8">
          {filteredPayments.map((method) => (
            <div key={method.region} className="bg-white rounded-2xl shadow-xl border border-gray-200 overflow-hidden hover:shadow-2xl transition-all duration-500">
              {/* Header */}
              <div className={`bg-gradient-to-r ${method.color} text-white p-8`}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <div className="bg-white/20 p-3 rounded-xl mr-4 backdrop-blur-sm">
                      {method.icon}
                    </div>
                    <div>
                      <h3 className="text-2xl font-bold">{method.name}</h3>
                      <p className="text-white/80">{method.methods.length} payment methods</p>
                    </div>
                  </div>
                  <button className="bg-white/20 hover:bg-white/30 px-6 py-2 rounded-full font-medium transition-all backdrop-blur-sm">
                    Enable
                  </button>
                </div>
              </div>

              {/* Methods List */}
              <div className="p-6">
                {method.methods.map((item, idx) => (
                  <div key={idx} className="flex items-center justify-between py-4 border-b border-gray-100 last:border-0 hover:bg-gray-50 transition-colors duration-200 px-2 rounded">
                    <div className="flex items-center">
                      <div className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center mr-4">
                        <span className="text-xl">{item.icon || item.symbol || 'üí≥'}</span>
                      </div>
                      <div>
                        <h4 className="font-bold text-gray-900">{item.name}</h4>
                        <p className="text-sm text-gray-500">{item.country || item.coverage || 'Global'}</p>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="font-bold text-gray-900">{item.fee} fee</div>
                      <div className="text-sm text-gray-500">{item.speed || 'Processing'}</div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Footer */}
              <div className="bg-gray-50 p-6 border-t border-gray-200">
                <button className="w-full bg-gradient-to-r from-blue-600 to-blue-700 text-white py-3 rounded-xl font-bold hover:from-blue-700 hover:to-blue-800 transition-all duration-300 shadow-md hover:shadow-lg">
                  Integrate {method.name}
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* CTA Section */}
        <div className="mt-20 bg-gradient-to-r from-green-500 to-emerald-600 rounded-3xl p-12 text-white text-center shadow-2xl">
          <h2 className="text-4xl font-bold mb-6">Ready to Go Global?</h2>
          <p className="text-xl mb-8 max-w-2xl mx-auto">
            Start accepting payments from 190+ countries with one simple integration.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button className="px-8 py-4 bg-white text-green-700 font-bold rounded-xl hover:bg-gray-100 transition-all duration-300 shadow-lg hover:shadow-xl">
              Start Free Trial
            </button>
            <button className="px-8 py-4 border-2 border-white text-white rounded-xl hover:bg-white/10 transition-all duration-300">
              Contact Sales
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GlobalPaymentsPage;
