import React, { createContext, useState, useContext, useEffect, useCallback, useMemo } from 'react';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // Load cart from localStorage on initial render
  useEffect(() => {
    try {
      const storedCart = localStorage.getItem('cart');
      if (storedCart) {
        const parsedCart = JSON.parse(storedCart);
        // Validate cart data structure
        if (Array.isArray(parsedCart)) {
          setCart(parsedCart);
        }
      }
    } catch (error) {
      console.error('Error loading cart from localStorage:', error);
      // Clear corrupted cart data
      localStorage.removeItem('cart');
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    if (!isLoading) {
      try {
        localStorage.setItem('cart', JSON.stringify(cart));
      } catch (error) {
        console.error('Error saving cart to localStorage:', error);
      }
    }
  }, [cart, isLoading]);

  const addToCart = useCallback((product) => {
    if (!product || !product.id) {
      console.error('Invalid product:', product);
      return;
    }
    
    setCart(prevCart => {
      const existingProduct = prevCart.find(item => item.id === product.id);
      if (existingProduct) {
        return prevCart.map(item =>
          item.id === product.id ? { 
            ...item, 
            quantity: item.quantity + 1 
          } : item
        );
      } else {
        return [...prevCart, { 
          ...product, 
          quantity: 1,
          addedAt: new Date().toISOString() // Track when item was added
        }];
      }
    });
  }, []);

  const removeFromCart = useCallback((productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  }, []);

  const updateQuantity = useCallback((productId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(productId);
    } else {
      setCart(prevCart =>
        prevCart.map(item =>
          item.id === productId ? { ...item, quantity: Math.max(1, quantity) } : item
        )
      );
    }
  }, [removeFromCart]);

  const clearCart = useCallback(() => {
    setCart([]);
  }, []);

  const toggleCart = useCallback(() => {
    setIsOpen(prev => !prev);
  }, []);

  const openCart = useCallback(() => {
    setIsOpen(true);
  }, []);

  const closeCart = useCallback(() => {
    setIsOpen(false);
  }, []);

  const getCartCount = useCallback(() => {
    return cart.reduce((count, item) => count + (item.quantity || 0), 0);
  }, [cart]);

  const getItemQuantity = useCallback((productId) => {
    const item = cart.find(item => item.id === productId);
    return item ? item.quantity : 0;
  }, [cart]);

  const isInCart = useCallback((productId) => {
    return cart.some(item => item.id === productId);
  }, [cart]);

  // Memoize cart total to prevent unnecessary recalculations
  const cartTotal = useMemo(() => {
    return cart.reduce((total, item) => {
      const price = parseFloat(item.price) || 0;
      const quantity = parseInt(item.quantity) || 0;
      return total + (price * quantity);
    }, 0);
  }, [cart]);

  const cartSubtotal = useMemo(() => cartTotal, [cartTotal]);

  // Calculate tax (example: 10% tax rate)
  const taxAmount = useMemo(() => cartTotal * 0.1, [cartTotal]);

  // Calculate shipping (example: free over $50, otherwise $5.99)
  const shippingAmount = useMemo(() => 
    cartTotal > 50 || cart.length === 0 ? 0 : 5.99, 
    [cartTotal, cart.length]
  );

  // Calculate grand total
  const grandTotal = useMemo(() => 
    cartTotal + taxAmount + shippingAmount, 
    [cartTotal, taxAmount, shippingAmount]
  );

  // Check if cart is empty
  const isCartEmpty = useMemo(() => cart.length === 0, [cart.length]);

  const value = useMemo(() => ({
    cart,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    isOpen,
    toggleCart,
    openCart,
    closeCart,
    getCartCount,
    getItemQuantity,
    isInCart,
    cartTotal,
    cartSubtotal,
    taxAmount,
    shippingAmount,
    grandTotal,
    isCartEmpty,
    isLoading
  }), [
    cart,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    isOpen,
    toggleCart,
    openCart,
    closeCart,
    getCartCount,
    getItemQuantity,
    isInCart,
    cartTotal,
    cartSubtotal,
    taxAmount,
    shippingAmount,
    grandTotal,
    isCartEmpty,
    isLoading
  ]);

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};

export default CartContext;
