// src/services/openaiService.js
class OpenAIService {
  constructor() {
    this.apiKey = localStorage.getItem('openai_api_key');
  }

  // Fixed function name - make it lowercase 'getFallbackPrice'
  getFallbackPrice = async (productName, originalPrice) => {
    try {
      if (!this.apiKey) {
        console.warn('No API key found. Using default price.');
        return this.generateSmartPrice(originalPrice);
      }

      // Simulate API call - in real app, you'd call OpenAI API
      // For now, return a smart calculated price
      return this.generateSmartPrice(originalPrice);
    } catch (error) {
      console.error('Error in getFallbackPrice:', error);
      return this.generateSmartPrice(originalPrice);
    }
  };

  // Alias for backward compatibility
  getFallbackPricing = async (productName, originalPrice) => {
    return this.getFallbackPrice(productName, originalPrice);
  };

  generateSmartPrice = (originalPrice) => {
    // Generate a smart price based on original price
    // Add some variation for AI pricing
    const variation = Math.random() * 0.2 - 0.1; // ±10%
    const smartPrice = originalPrice * (1 + variation);
    return Math.round(smartPrice * 100) / 100; // Round to 2 decimals
  };

  analyzeMarketTrends = async (category) => {
    const trends = {
      'electronics': {
        trending: ['wireless earbuds', 'smart watches', 'gaming laptops'],
        growth: 12.5,
        forecast: 'Upward trend for Q1 2026'
      },
      'automotive': {
        trending: ['EV accessories', 'car audio', 'performance parts'],
        growth: 8.3,
        forecast: 'Stable growth expected'
      },
      'fashion': {
        trending: ['sustainable clothing', 'techwear', 'smart accessories'],
        growth: 15.7,
        forecast: 'Strong seasonal demand'
      }
    };
    
    return trends[category] || {
      trending: ['online shopping', 'fast delivery', 'eco-friendly products'],
      growth: 10.2,
      forecast: 'General market growth'
    };
  };

  generateSEOKeywords = async () => {
    const currentDate = new Date().toLocaleDateString('en-GB', {
      day: '2-digit',
      month: 'long',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      timeZone: 'Europe/London'
    });

    return {
      date: currentDate,
      keywords: [
        { keyword: 'UK next day delivery 2026', change: 45, volume: 'High' },
        { keyword: 'best price AI comparison', change: 68, volume: 'Very High' },
        { keyword: 'secure mobile payments UK', change: 32, volume: 'Medium' },
        { keyword: 'eco-friendly packaging 2026', change: 78, volume: 'High' },
        { keyword: 'live price tracking AI', change: 52, volume: 'Medium' },
        { keyword: 'VR shopping experience', change: 89, volume: 'Growing' },
        { keyword: 'AI-powered recommendations', change: 63, volume: 'High' },
        { keyword: 'contactless delivery options', change: 41, volume: 'Medium' }
      ]
    };
  };

  getAIActionItems = () => {
    return [
      {
        id: 1,
        task: 'Optimize page titles with current trending keywords',
        priority: 'high',
        estimatedTime: '2 hours',
        impact: 'High SEO improvement'
      },
      {
        id: 2,
        task: 'Implement lazy loading for faster page speed',
        priority: 'high',
        estimatedTime: '4 hours',
        impact: '40% faster load time'
      },
      {
        id: 3,
        task: 'Add structured data for rich snippets',
        priority: 'medium',
        estimatedTime: '3 hours',
        impact: 'Better search visibility'
      },
      {
        id: 4,
        task: 'Optimize images with WebP format',
        priority: 'medium',
        estimatedTime: '5 hours',
        impact: '30% smaller file sizes'
      },
      {
        id: 5,
        task: 'Implement Core Web Vitals improvements',
        priority: 'high',
        estimatedTime: '8 hours',
        impact: 'Better user experience score'
      },
      {
        id: 6,
        task: 'Add AI-powered product recommendations',
        priority: 'medium',
        estimatedTime: '6 hours',
        impact: '25% higher conversion'
      }
    ];
  };
}

// Export as singleton
export default new OpenAIService();
