// frontend/src/pages/PaymentGateway.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './PaymentMethodsPage.css';

const PaymentGateway = () => {
  const [isGlobalPaymentsOpen, setIsGlobalPaymentsOpen] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState('Global');
  const [marketAnalysis, setMarketAnalysis] = useState(null);
  const [seoScore, setSeoScore] = useState(0);
  const paymentsRef = useRef(null);
  const navigate = useNavigate();

  const paymentMethods = {
    'Global': ['Credit/Debit Cards', 'PayPal', 'Apple Pay', 'Google Pay', 'Cryptocurrency', 'Bank Transfer', 'Alipay', 'WeChat Pay'],
    'UK': ['Credit/Debit Cards', 'PayPal', 'Apple Pay', 'Google Pay', 'Direct Debit', 'Faster Payments'],
    'US': ['Credit/Debit Cards', 'PayPal', 'Apple Pay', 'Google Pay', 'Venmo', 'Zelle', 'ACH Transfer'],
    'China': ['Alipay', 'WeChat Pay', 'UnionPay', 'JD Pay', 'Bank Transfer'],
    'Japan': ['Konbini Payments', 'Credit Cards', 'PayPay', 'Rakuten Pay', 'Bank Transfer'],
    'EU': ['Credit/Debit Cards', 'PayPal', 'iDEAL', 'SOFORT', 'Giropay', 'SEPA']
  };

  const marketData = {
    'Global': { accuracy: '98.7%', volume: '$12.85B', trend: '-0.47%' },
    'UK': { accuracy: '96.3%', volume: '¬£8.85B', trend: '-0.06%' },
    'China': { accuracy: '97.5%', volume: '¬•102.3B', trend: '-0.36%' },
    'Japan': { accuracy: '95.8%', volume: '¬•9.42B', trend: '+0.12%' },
    'US': { accuracy: '99.1%', volume: '$15.2B', trend: '+0.23%' }
  };

  const toggleGlobalPayments = () => {
    setIsGlobalPaymentsOpen(!isGlobalPaymentsOpen);
    if (!isGlobalPaymentsOpen && paymentsRef.current) {
      setTimeout(() => {
        paymentsRef.current.scrollIntoView({ behavior: 'smooth' });
      }, 100);
    }
  };

  const analyzeSEO = () => {
    // Simulate SEO analysis
    const score = Math.floor(Math.random() * 30) + 70;
    setSeoScore(score);
    return {
      score,
      suggestions: [
        'Optimize payment page load time',
        'Add structured data for payment methods',
        'Improve mobile responsiveness',
        'Add hreflang tags for international payments'
      ]
    };
  };

  useEffect(() => {
    // Analyze SEO on component mount
    const seoData = analyzeSEO();
    console.log('SEO Analysis:', seoData);
    
    // Set initial market analysis
    setMarketAnalysis(marketData[selectedCountry]);
    
    // Scroll to top on mount
    window.scrollTo(0, 0);
  }, [selectedCountry]);

  return (
    <div className="payment-gateway-container">
      {/* Top Navigation Button */}
      <div className="payment-top-nav">
        <button 
          className="global-payments-toggle-btn active"
          onClick={toggleGlobalPayments}
        >
          üåç Global Payments Dashboard
        </button>
        
        <div className="country-selector">
          {['Global', 'UK', 'US', 'China', 'Japan', 'EU'].map(country => (
