          <div className="payments-grid">
            {paymentMethods[selectedCountry]?.map((method, index) => (
              <div key={index} className="payment-method-card">
                <div className="payment-icon">
                  {method.includes('Card') ? 'üí≥' : 
                   method === 'PayPal' ? 'üîµ' : 
                   method.includes('Apple') ? 'üçé' : 
                   method.includes('Google') ? 'üî¥' : 
                   method.includes('Crypto') ? '‚Çø' : 
                   method.includes('Ali') ? 'üí∞' : 
                   method.includes('WeChat') ? 'üí¨' : 'üè¶'}
