                  <h3>{method}</h3>
                  <p className="coverage">Global Coverage: {Math.floor(Math.random() * 50) + 50}%</p>
                  <p className="fee">Fee: {Math.random() > 0.5 ? '0% - 2.9%' : 'Fixed $0.30'}</p>
                </div>
                <button className="select-btn" onClick={() => navigate('/checkout', { state: { method } })}>
