import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { QueryClient, QueryClientProvider } from 'react-query';
import { HelmetProvider } from 'react-helmet-async';
import { CartProvider } from './context/CartContext';

// MODERN LAYOUT COMPONENTS
import ModernHeader from './components/ModernHeader';
import ModernNavbar from './components/ModernNavbar';
import ModernFooter from './components/ModernFooter';

// MODERN PAGES
import Home from './pages/Home';
import Products from './pages/Products';
import ProductDetailPage from './pages/ProductDetailPage';
import CartPage from './pages/CartPage';
import CheckoutPage from './pages/CheckoutPage';
import CheckoutSuccessPage from './pages/CheckoutSuccessPage';
import GlobalPaymentsPage from './pages/GlobalPaymentsPage'; // This is the only payment page
import MarketDashboard from './pages/MarketDashboard';
import GlobalAnalysis from './pages/GlobalAnalysis';
import PaymentGateway from './pages/PaymentGateway';
import SEOAnalyzer from './pages/SEOAnalyzer';
import AutomotivePartsPage from './pages/AutomotivePartsPage';
import ElectronicsPage from './pages/ElectronicsPage';
import CategoryPage from './pages/CategoryPage';
import ContactPage from './pages/ContactPage';
import NotFoundPage from './pages/NotFoundPage';
import OrderSuccessPage from './pages/OrderSuccessPage';
import FileUploadPage from './pages/FileUpload';

// MODERN COMPONENTS
import CartSidebar from './components/CartSidebar';
import AuthModal from './components/AuthModal';
import PaymentModal from './components/PaymentModal';

// STYLES
import './App.css';

// Create React Query client
const queryClient = new QueryClient();

function App() {
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isPaymentModalOpen, setIsPaymentModalOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [showApiKeyModal, setShowApiKeyModal] = useState(false);
  const [apiKey, setApiKey] = useState('');

  // Initialize from localStorage
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    const savedKey = localStorage.getItem('openai_api_key');

    if (savedUser) {
      try {
        setUser(JSON.parse(savedUser));
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    }

    if (savedKey) {
      setApiKey(savedKey);
    }
  }, []);

  // Handle API Key Submission
  const handleApiKeySubmit = () => {
    if (apiKey && apiKey.startsWith('sk-')) {
      localStorage.setItem('openai_api_key', apiKey);
      setShowApiKeyModal(false);
      // Show success message
      if (window.showToast) {
        window.showToast('API Key saved successfully!', 'success');
      }
    } else {
      if (window.showToast) {
        window.showToast('Please enter a valid OpenAI API key (starts with sk-)', 'error');
      }
    }
  };

  // Handle User Login
  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthModalOpen(false);

    if (window.showToast) {
      window.showToast(`Welcome back, ${userData.name || userData.email}!`, 'success');
    }
  };

  // Handle User Logout
  const handleLogout = () => {
    const userName = user?.name || user?.email?.split('@')[0];
    setUser(null);
    localStorage.removeItem('user');

    if (window.showToast) {
      window.showToast(`Goodbye, ${userName}!`, 'info');
    }
  };

  // Global toast function
  useEffect(() => {
    window.showToast = (message, type = 'info') => {
      const event = new CustomEvent('showToast', {
        detail: { message, type }
      });
      window.dispatchEvent(event);
    };

    return () => {
      delete window.showToast;
    };
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <HelmetProvider>
        <CartProvider>
          <Router>
            <div className="App min-h-screen flex flex-col bg-gray-50">
              {/* Global Toaster */}
              <Toaster 
                position="top-right"
                toastOptions={{
                  duration: 4000,
                  style: {
                    background: '#1f2937',
                    color: '#fff',
                    borderRadius: '12px',
                    padding: '16px',
                    boxShadow: '0 10px 25px rgba(0, 0, 0, 0.2)',
                  },
                  success: {
                    style: {
                      background: '#059669',
                      borderLeft: '4px solid #10b981',
                    },
                    icon: 'âœ…',
                  },
                  error: {
                    style: {
                      background: '#dc2626',
                      borderLeft: '4px solid #ef4444',
                    },
                    icon: 'âŒ',
                  },
                }}
              />

              {/* Modern Header */}
              <ModernHeader 
                onCartClick={() => setIsCartOpen(true)}
                onLoginClick={() => setIsAuthModalOpen(true)}
                user={user}
                onLogout={handleLogout}
              />

              {/* Modern Navigation */}
              <ModernNavbar />

              {/* Main Content Area */}
              <main className="flex-grow">
                <Routes>
                  {/* Home & Core Pages */}
                  <Route path="/" element={<Home />} />
                  <Route path="/products" element={<Products />} />
                  <Route path="/product/:id" element={<ProductDetailPage />} />

                  {/* Cart & Checkout */}
                  <Route path="/cart" element={<CartPage />} />
                  <Route path="/checkout" element={<CheckoutPage />} />
                  <Route path="/checkout-success" element={<CheckoutSuccessPage />} />
                  <Route path="/order-success" element={<OrderSuccessPage />} />

                  {/* Global Payments & Financial - ONLY ONE PAYMENT PAGE */}
                  <Route path="/global-payments" element={<GlobalPaymentsPage />} />
                  <Route path="/payment-gateway" element={<PaymentGateway />} />

                  {/* Market & Analysis */}
                  <Route path="/market-dashboard" element={<MarketDashboard />} />
                  <Route path="/global-analysis" element={<GlobalAnalysis />} />

                  {/* Categories */}
                  <Route path="/automotive-parts" element={<AutomotivePartsPage />} />
                  <Route path="/electronics" element={<ElectronicsPage />} />
                  <Route path="/category/:category" element={<CategoryPage />} />

                  {/* Tools & Services */}
                  <Route path="/seo-analyzer" element={<SEOAnalyzer />} />
                  <Route path="/file-upload" element={<FileUploadPage />} />

                  {/* Company & Support */}
                  <Route path="/contact" element={<ContactPage />} />

                  {/* 404 Page */}
                  <Route path="*" element={<NotFoundPage />} />
                </Routes>
              </main>

              {/* Modern Footer */}
              <ModernFooter />

              {/* Modals & Sidebars */}
              <CartSidebar 
                isOpen={isCartOpen}
                onClose={() => setIsCartOpen(false)}
                onCheckout={() => {
                  setIsCartOpen(false);
                  setIsPaymentModalOpen(true);
                }}
              />

              <AuthModal 
                isOpen={isAuthModalOpen}
                onClose={() => setIsAuthModalOpen(false)}
                onLogin={handleLogin}
              />

              <PaymentModal 
                isOpen={isPaymentModalOpen}
                onClose={() => setIsPaymentModalOpen(false)}
              />

              {/* API Key Configuration Modal */}
              {showApiKeyModal && (
                <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
                  <div className="bg-white rounded-2xl max-w-md w-full shadow-2xl animate-fadeIn">
                    <div className="p-8">
                      <div className="flex items-center gap-3 mb-6">
                        <div className="p-3 bg-blue-100 rounded-xl">
                          <span className="text-2xl">ðŸ”‘</span>
                        </div>
                        <div>
                          <h2 className="text-2xl font-bold">OpenAI API Configuration</h2>
                          <p className="text-gray-500">Enable AI-powered features</p>
                        </div>
                      </div>

                      <div className="mb-6">
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                          API Key <span className="text-gray-500 text-xs">(starts with sk-)</span>
                        </label>
                        <input
                          type="password"
                          value={apiKey}
                          onChange={(e) => setApiKey(e.target.value)}
                          placeholder="sk-..."
                          className="w-full p-4 border-2 border-gray-200 rounded-xl focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all"
                          autoFocus
                        />
                      </div>

                      <div className="flex gap-3">
                        <button
                          onClick={handleApiKeySubmit}
                          className="flex-1 bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-4 rounded-xl font-semibold hover:from-blue-700 hover:to-indigo-700 transition-all"
                        >
                          Save Key
                        </button>
                        <button
                          onClick={() => setShowApiKeyModal(false)}
                          className="flex-1 bg-gray-100 text-gray-700 py-4 rounded-xl font-semibold hover:bg-gray-200 transition-all"
                        >
                          Cancel
                        </button>
                      </div>

                      <div className="mt-6 p-4 bg-gray-50 rounded-xl border border-gray-200">
                        <h4 className="font-semibold text-gray-800 mb-2">How to get your API key:</h4>
                        <ol className="list-decimal pl-5 text-sm text-gray-600 space-y-1">
                          <li>Visit <a href="https://platform.openai.com/api-keys" target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">OpenAI API Keys</a></li>
                          <li>Sign in or create an account</li>
                          <li>Click "Create new secret key"</li>
                          <li>Copy and paste above</li>
                        </ol>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </Router>
        </CartProvider>
      </HelmetProvider>
    </QueryClientProvider>
  );
}

export default App;
