import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Upload, BarChart3, TrendingUp, DollarSign, Globe, 
  FileText, AlertCircle, CheckCircle, X, Zap,
  CreditCard, Activity, Shield, Target, Users,
  TrendingDown, RefreshCw, Database, PieChart,
  Smartphone, ShoppingBag, Car, Home, Watch,
  ChevronRight, ExternalLink, Download, Filter
} from 'lucide-react';
import FileUpload from '../components/FileUpload';
import './MarketDashboard.css';

const MarketDashboard = () => {
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedRegion, setSelectedRegion] = useState('global');
  const [timeframe, setTimeframe] = useState('realtime');
  const [productInput, setProductInput] = useState('');
  const [categoryInput, setCategoryInput] = useState('');
  const [liveData, setLiveData] = useState(true);
  const [marketTrends, setMarketTrends] = useState([]);
  const navigate = useNavigate();

  // Enhanced regions with market data
  const regions = [
    { 
      id: 'global', 
      name: 'ðŸŒ Global', 
      color: 'from-blue-500 to-cyan-500',
      volume: '$12.85B',
      trend: '-0.47%',
      accuracy: '98.7%'
    },
    { 
      id: 'usa', 
      name: 'ðŸ‡ºðŸ‡¸ USA', 
      color: 'from-red-500 to-blue-600',
      volume: '$15.2B',
      trend: '+0.23%',
      accuracy: '99.1%'
    },
    { 
      id: 'uk', 
      name: 'ðŸ‡¬ðŸ‡§ UK', 
      color: 'from-blue-600 to-indigo-700',
      volume: 'Â£8.85B',
      trend: '-0.06%',
      accuracy: '96.3%'
    },
    { 
      id: 'china', 
      name: 'ðŸ‡¨ðŸ‡³ China', 
      color: 'from-red-600 to-red-700',
      volume: 'Â¥102.3B',
      trend: '-0.36%',
      accuracy: '97.5%'
    },
    { 
      id: 'japan', 
      name: 'ðŸ‡¯ðŸ‡µ Japan', 
      color: 'from-white to-red-500',
      volume: 'Â¥9.42B',
      trend: '+0.12%',
      accuracy: '95.8%'
    },
    { 
      id: 'eu', 
      name: 'ðŸ‡ªðŸ‡º Europe', 
      color: 'from-yellow-500 to-blue-600',
      volume: 'â‚¬14.3B',
      trend: '-0.18%',
      accuracy: '97.2%'
    },
  ];

  // Top categories
  const topCategories = [
    { name: 'Electronics', icon: <Smartphone size={20} />, volume: '$4.2B', growth: '+15.2%' },
    { name: 'Classic Cars', icon: <Car size={20} />, volume: '$2.8B', growth: '+8.7%' },
    { name: 'Fashion', icon: <ShoppingBag size={20} />, volume: '$3.1B', growth: '+12.3%' },
    { name: 'Home', icon: <Home size={20} />, volume: '$1.9B', growth: '+5.4%' },
    { name: 'Watches', icon: <Watch size={20} />, volume: '$0.8B', growth: '+18.9%' },
  ];

  useEffect(() => {
    // Simulate live data updates
    const interval = setInterval(() => {
      if (liveData) {
        fetchMarketTrends();
      }
    }, 30000); // Update every 30 seconds

    fetchMarketTrends();
    return () => clearInterval(interval);
  }, [liveData]);

  const fetchMarketTrends = async () => {
    // Mock market trends data
    const mockTrends = [
      { id: 1, name: 'iPhone 15 Pro', category: 'Electronics', trend: 'Rising', change: '+12.5%', price: '$1,299' },
      { id: 2, name: 'Vintage Rolex', category: 'Watches', trend: 'Hot', change: '+25.3%', price: '$18,500' },
      { id: 3, name: 'Tesla Model 3', category: 'Cars', trend: 'Stable', change: '+2.1%', price: '$42,990' },
      { id: 4, name: 'MacBook Air M2', category: 'Electronics', trend: 'Rising', change: '+8.7%', price: '$1,199' },
      { id: 5, name: 'Designer Handbags', category: 'Fashion', trend: 'Hot', change: '+18.9%', price: '$2,500' },
    ];
    setMarketTrends(mockTrends);
  };

  const analyzeMarket = async (product, category) => {
    setLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Enhanced mock analysis data
      const mockAnalysis = {
        timestamp: new Date().toISOString(),
        ai_confidence: '99.7%',
        global_price_analysis: {
          average_price: "$1,299",
          price_range: "$999 - $1,599",
          best_buying_region: "Asia",
          best_selling_region: "Europe",
          price_volatility: "Low"
        },
        high_demand_items: [
          { item: "iPhone 15 Pro", profit_margin: "45%", demand_score: "9.2/10", avg_sell_time: "2.3 days" },
          { item: "MacBook Air M2", profit_margin: "38%", demand_score: "8.7/10", avg_sell_time: "3.1 days" },
          { item: "Samsung Galaxy S24", profit_margin: "42%", demand_score: "8.9/10", avg_sell_time: "1.9 days" }
        ],
        market_insights: [
          "Mobile electronics show 35% higher profit margins in EU",
          "Seasonal trends indicate 42% price increase for Q4",
          "Supply chain optimized for 15% cost reduction"
        ],
        recommendations: [
          "Increase stock of trending electronics by 25%",
          "Adjust pricing strategy for European market (+18% markup)",
          "Consider bundling accessories (increases conversion by 32%)",
          "Optimize payment methods for target regions"
        ],
        risk_assessment: {
          overall_risk: "Low",
          competition_risk: "Medium",
          supply_risk: "Low",
          demand_risk: "Low"
        }
      };
      
      setAnalysis(mockAnalysis);
    } catch (error) {
      console.error('Analysis failed:', error);
      alert('Analysis failed. Please check your connection or try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = (files) => {
    console.log('Files uploaded:', files);
    alert(`${Array.isArray(files) ? files.length : 1} file(s) uploaded successfully!`);
  };

  const handleQuickAnalyze = () => {
    if (!productInput.trim() || !categoryInput.trim()) {
      alert('Please enter both product name and category');
      return;
    }
    analyzeMarket(productInput, categoryInput);
  };

  const handleGlobalPaymentsClick = () => {
    navigate('/payment-gateway');
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const exportAnalysis = () => {
    if (!analysis) return;
    const dataStr = JSON.stringify(analysis, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    const exportFileDefaultName = `market-analysis-${new Date().toISOString().split('T')[0]}.json`;
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  return (
    <div className="market-dashboard">
      {/* Header Section */}
      <div className="dashboard-header">
        <div className="header-content">
          <div className="header-main">
            <h1>
              <span className="header-icon">ðŸŽ¯</span>
              <span className="header-title">AI Market Intelligence Dashboard</span>
            </h1>
            <p className="header-subtitle">Real-time Analysis â€¢ 99.9% Accuracy â€¢ Global Coverage</p>
          </div>
          
          <div className="header-actions">
            <button 
              className="btn-global-payments"
              onClick={handleGlobalPaymentsClick}
            >
              <CreditCard size={18} />
              <span>Global Payments</span>
              <span className="live-badge">
                <Activity size={10} /> LIVE
              </span>
            </button>
            
            <div className="live-toggle">
              <div className={`toggle-switch ${liveData ? 'active' : ''}`} onClick={() => setLiveData(!liveData)}>
                <div className="toggle-slider"></div>
              </div>
              <span className="toggle-label">
                <Activity size={14} /> Live Data
              </span>
            </div>
          </div>
        </div>

        {/* Stats Overview */}
        <div className="stats-grid">
          <div className="stat-card profit">
            <div className="stat-icon">ðŸ’°</div>
            <div className="stat-content">
              <div className="stat-value">35-60%</div>
              <div className="stat-label">Avg. Profit Margin</div>
              <div className="stat-trend positive">â†‘ 8.2%</div>
            </div>
          </div>
          
          <div className="stat-card volume">
            <div className="stat-icon">ðŸ“ˆ</div>
            <div className="stat-content">
              <div className="stat-value">$128.5B</div>
              <div className="stat-label">Global Volume</div>
              <div className="stat-trend positive">â†‘ 12.5%</div>
            </div>
          </div>
          
          <div className="stat-card accuracy">
            <div className="stat-icon">ðŸŽ¯</div>
            <div className="stat-content">
              <div className="stat-value">99.7%</div>
              <div className="stat-label">AI Accuracy</div>
              <div className="stat-trend stable">Â±0.1%</div>
            </div>
          </div>
          
          <div className="stat-card speed">
            <div className="stat-icon">âš¡</div>
            <div className="stat-content">
              <div className="stat-value">2.3s</div>
              <div className="stat-label">Avg. Analysis Time</div>
              <div className="stat-trend positive">â†“ 0.4s</div>
            </div>
          </div>
        </div>
      </div>

      {/* Region & Market Overview */}
      <div className="region-section">
        <div className="section-header">
          <h2>
            <Globe size={24} />
            <span>ðŸŒ Global Market Overview</span>
          </h2>
          <div className="timeframe-selector">
            <button className={timeframe === 'realtime' ? 'active' : ''} onClick={() => setTimeframe('realtime')}>
              Real-time
            </button>
            <button className={timeframe === 'daily' ? 'active' : ''} onClick={() => setTimeframe('daily')}>
              24h
            </button>
            <button className={timeframe === 'weekly' ? 'active' : ''} onClick={() => setTimeframe('weekly')}>
              7D
            </button>
            <button className={timeframe === 'monthly' ? 'active' : ''} onClick={() => setTimeframe('monthly')}>
              30D
            </button>
          </div>
        </div>

        <div className="region-grid">
          {regions.map(region => (
            <div 
              key={region.id}
              className={`region-card ${selectedRegion === region.id ? 'selected' : ''}`}
              onClick={() => setSelectedRegion(region.id)}
            >
              <div className="region-header">
                <div className="region-name">
                  <span className="region-flag">{region.name.split(' ')[0]}</span>
                  <span className="region-title">{region.name.split(' ')[1]}</span>
                </div>
                {selectedRegion === region.id && (
                  <div className="region-selected">
                    <CheckCircle size={16} />
                  </div>
                )}
              </div>
              
              <div className="region-data">
                <div className="data-item">
                  <span className="data-label">Volume:</span>
                  <span className="data-value">{region.volume}</span>
                </div>
                <div className="data-item">
                  <span className="data-label">Trend:</span>
                  <span className={`data-trend ${region.trend.startsWith('+') ? 'positive' : 'negative'}`}>
                    {region.trend}
                  </span>
                </div>
                <div className="data-item">
                  <span className="data-label">Accuracy:</span>
                  <span className="data-accuracy">{region.accuracy}</span>
                </div>
              </div>
              
              <div className="region-footer">
                <span className="ai-update">
                  <Zap size={12} /> AI Updated 42m ago
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Dashboard Grid */}
      <div className="dashboard-grid">
        {/* Left Column - Analysis Tools */}
        <div className="dashboard-column main-column">
          {/* Quick Analysis Card */}
          <div className="dashboard-card">
            <div className="card-header">
              <BarChart3 size={24} />
              <h3>ðŸ” Quick Product Analysis</h3>
            </div>
            
            <div className="analysis-form">
              <div className="form-group">
                <label>Product Name</label>
                <input
                  type="text"
                  value={productInput}
                  onChange={(e) => setProductInput(e.target.value)}
                  placeholder="e.g., iPhone 15 Pro, Vintage Watch, etc."
                  className="form-input"
                />
              </div>
              
              <div className="form-group">
                <label>Category</label>
                <div className="category-tags">
                  {topCategories.map((cat, idx) => (
                    <button
                      key={idx}
                      className={`category-tag ${categoryInput === cat.name ? 'active' : ''}`}
                      onClick={() => setCategoryInput(cat.name)}
                    >
                      {cat.icon}
                      {cat.name}
                    </button>
                  ))}
                </div>
                <input
                  type="text"
                  value={categoryInput}
                  onChange={(e) => setCategoryInput(e.target.value)}
                  placeholder="Or enter custom category"
                  className="form-input"
                />
              </div>
              
              <button 
                onClick={handleQuickAnalyze}
                disabled={loading}
                className="btn-analyze"
              >
                {loading ? (
                  <>
                    <div className="spinner"></div>
                    Analyzing Market...
                  </>
                ) : (
                  <>
                    <Zap size={18} />
                    Analyze with AI
                  </>
                )}
              </button>
            </div>
          </div>

          {/* File Upload Section */}
          <div className="dashboard-card">
            <div className="card-header">
              <Upload size={24} />
              <h3>ðŸ“ Bulk Data Upload</h3>
            </div>
            
            <FileUpload 
              onUpload={handleFileUpload}
              accept="image/*,.csv,.xlsx,.pdf,.json"
              multiple={true}
              maxSize={50}
            />
            
            <div className="upload-tips">
              <div className="tip-header">
                <AlertCircle size={18} />
                <span>Upload Guidelines</span>
              </div>
              <ul>
                <li>â€¢ CSV/Excel: Include Product, Price, Category, Quantity</li>
                <li>â€¢ Images: JPG/PNG up to 5MB each</li>
                <li>â€¢ PDF: Product catalogs and specifications</li>
                <li>â€¢ Max total size: 50MB per upload</li>
              </ul>
            </div>
          </div>

          {/* Market Trends */}
          <div className="dashboard-card">
            <div className="card-header">
              <TrendingUp size={24} />
              <h3>ðŸ”¥ Live Market Trends</h3>
            </div>
            
            <div className="trends-list">
              {marketTrends.map(trend => (
                <div key={trend.id} className="trend-item">
                  <div className="trend-info">
                    <div className="trend-name">{trend.name}</div>
                    <div className="trend-category">{trend.category}</div>
                  </div>
                  <div className="trend-stats">
                    <span className={`trend-status ${trend.trend.toLowerCase()}`}>
                      {trend.trend}
                    </span>
                    <span className={`trend-change ${trend.change.startsWith('+') ? 'positive' : 'negative'}`}>
                      {trend.change}
                    </span>
                    <span className="trend-price">{trend.price}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Right Column - Insights */}
        <div className="dashboard-column sidebar-column">
          {/* Top Categories */}
          <div className="dashboard-card">
            <div className="card-header">
              <PieChart size={24} />
              <h3>ðŸ“Š Top Categories</h3>
            </div>
            
            <div className="categories-list">
              {topCategories.map((cat, idx) => (
                <div key={idx} className="category-item">
                  <div className="category-header">
                    {cat.icon}
                    <span className="category-name">{cat.name}</span>
                  </div>
                  <div className="category-stats">
                    <span className="category-volume">{cat.volume}</span>
                    <span className={`category-growth ${cat.growth.startsWith('+') ? 'positive' : 'negative'}`}>
                      {cat.growth}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Action Required */}
          <div className="dashboard-card actions-card">
            <div className="card-header">
              <AlertCircle size={24} />
              <h3>âš¡ Action Required</h3>
            </div>
            
            <div className="actions-list">
              {[
                { id: 1, text: 'Update prices for holiday season', priority: 'high', completed: true },
                { id: 2, text: 'Add mobile payment options', priority: 'high', completed: true },
                { id: 3, text: 'Stock running low on popular items', priority: 'medium', completed: false },
                { id: 4, text: 'Review competitor pricing strategy', priority: 'medium', completed: false },
                { id: 5, text: 'Optimize product listings for SEO', priority: 'low', completed: false },
                { id: 6, text: 'Configure global payment methods', priority: 'high', completed: false, onClick: handleGlobalPaymentsClick },
              ].map((action) => (
                <div 
                  key={action.id} 
                  className={`action-item ${action.priority} ${action.completed ? 'completed' : ''}`}
                  onClick={action.onClick}
                >
                  <div className="action-checkbox">
                    {action.completed ? (
                      <CheckCircle size={16} />
                    ) : (
                      <div className="checkbox"></div>
                    )}
                  </div>
                  <div className="action-text">{action.text}</div>
                  {action.onClick && <ChevronRight size={16} />}
                </div>
              ))}
            </div>
          </div>

          {/* Payment Quick Actions */}
          <div className="dashboard-card payments-card">
            <div className="card-header">
              <CreditCard size={24} />
              <h3>ðŸ’³ Payment Insights</h3>
            </div>
            
            <div className="payments-content">
              <div className="payment-stats">
                <div className="payment-stat">
                  <div className="stat-label">Success Rate</div>
                  <div className="stat-value">98.7%</div>
                </div>
                <div className="payment-stat">
                  <div className="stat-label">Avg. Fee</div>
                  <div className="stat-value">2.3%</div>
                </div>
                <div className="payment-stat">
                  <div className="stat-label">Process Time</div>
                  <div className="stat-value">2.1s</div>
                </div>
              </div>
              
              <button className="btn-view-payments" onClick={handleGlobalPaymentsClick}>
                <CreditCard size={18} />
                View Global Payments
                <ExternalLink size={16} />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Analysis Results */}
      {analysis && (
        <div className="analysis-results">
          <div className="results-header">
            <h2>ðŸ“ˆ AI Analysis Results</h2>
            <div className="results-actions">
              <button className="btn-export" onClick={exportAnalysis}>
                <Download size={18} />
                Export JSON
              </button>
              <button className="btn-close" onClick={() => setAnalysis(null)}>
                <X size={20} />
              </button>
            </div>
          </div>
          
          <div className="results-grid">
            <div className="result-section">
              <h4>ðŸ’° Price Analysis</h4>
              <div className="result-items">
                {Object.entries(analysis.global_price_analysis).map(([key, value]) => (
                  <div key={key} className="result-item">
                    <span className="item-label">{key.replace('_', ' ')}:</span>
                    <span className="item-value">{value}</span>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="result-section">
              <h4>ðŸ”¥ High Demand Items</h4>
              <div className="result-items">
                {analysis.high_demand_items.map((item, idx) => (
                  <div key={idx} className="demand-item">
                    <div className="demand-header">
                      <span className="demand-name">{item.item}</span>
                      <span className="demand-margin">{item.profit_margin}</span>
                    </div>
                    <div className="demand-details">
                      <span>Demand: {item.demand_score}</span>
                      <span>Sell Time: {item.avg_sell_time}</span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            
            <div className="result-section full-width">
              <h4>ðŸ’¡ AI Recommendations</h4>
              <div className="recommendations-grid">
                {analysis.recommendations.map((rec, idx) => (
                  <div key={idx} className="recommendation-card">
                    <div className="rec-number">#{idx + 1}</div>
                    <div className="rec-text">{rec}</div>
                    {rec.includes('payment') && (
                      <button className="rec-action" onClick={handleGlobalPaymentsClick}>
                        Configure
                      </button>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
          
          <div className="ai-confidence">
            <div className="confidence-badge">
              <Zap size={16} />
              AI Confidence: {analysis.ai_confidence}
            </div>
            <div className="confidence-timestamp">
              Generated: {new Date(analysis.timestamp).toLocaleString()}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MarketDashboard;
