import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import './PaymentMethodsPage.css';

function PaymentMethodsPage() {
  const navigate = useNavigate();
  const { getCartTotal } = useCart();
  const [selectedMethod, setSelectedMethod] = useState('card');
  const [cardDetails, setCardDetails] = useState({
    number: '',
    expiry: '',
    cvv: '',
    name: ''
  });
  const [isProcessing, setIsProcessing] = useState(false);
  
  const totalAmount = getCartTotal();
  
  const paymentMethods = [
    { id: 'card', name: 'Credit/Debit Card', icon: 'üí≥', description: '3D Secure Supported' },
    { id: 'paypal', name: 'PayPal', icon: 'üì±', description: 'Fast & Secure' },
    { id: 'bank', name: 'Bank Transfer', icon: 'üè¶', description: 'Direct bank payment' },
    { id: 'crypto', name: 'Cryptocurrency', icon: '‚Çø', description: 'Bitcoin, Ethereum' },
    { id: 'mobile', name: 'Mobile Money', icon: 'üì≤', description: 'M-Pesa, Airtel Money' },
  ];
  
  const handleCardInputChange = (e) => {
    const { name, value } = e.target;
    setCardDetails(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  const handleProceedToPayment = async () => {
    if (selectedMethod === 'card') {
      if (!cardDetails.number || !cardDetails.expiry || !cardDetails.cvv || !cardDetails.name) {
        alert('Please fill all card details');
        return;
      }
    }
    
    setIsProcessing(true);
    
    // Simulate payment processing
    setTimeout(() => {
      setIsProcessing(false);
      navigate('/checkout-success');
    }, 2000);
  };
  
  const handleBackToCart = () => {
    navigate('/cart');
  };
  
  return (
    <div className="payment-methods-page">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-center mb-2">Payment Methods</h1>
        <p className="text-gray-600 text-center mb-8">Choose your preferred payment method</p>
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Left Column - Order Summary */}
          <div className="bg-gray-50 p-6 rounded-lg">
            <h2 className="text-xl font-semibold mb-4">Order Summary</h2>
            <div className="space-y-3 mb-6">
              <div className="flex justify-between">
                <span>Subtotal</span>
                <span>¬£{totalAmount.toFixed(2)}</span>
              </div>
              <div className="flex justify-between">
                <span>Shipping</span>
                <span>¬£5.99</span>
              </div>
              <div className="flex justify-between">
                <span>Tax</span>
                <span>¬£{(totalAmount * 0.2).toFixed(2)}</span>
              </div>
              <div className="border-t pt-3">
                <div className="flex justify-between text-lg font-bold">
                  <span>Total</span>
                  <span>¬£{(totalAmount + 5.99 + (totalAmount * 0.2)).toFixed(2)}</span>
                </div>
              </div>
            </div>
            
            <button
              onClick={handleBackToCart}
              className="w-full py-3 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition-colors mb-4"
            >
              ‚Üê Back to Cart
            </button>
          </div>
          
          {/* Right Column - Payment Methods */}
          <div className="space-y-6">
            {/* Payment Method Selection */}
            <div>
              <h2 className="text-xl font-semibold mb-4">Select Payment Method</h2>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                {paymentMethods.map(method => (
                  <button
                    key={method.id}
                    onClick={() => setSelectedMethod(method.id)}
                    className={`p-4 border rounded-lg text-center transition-all ${
                      selectedMethod === method.id
                        ? 'border-blue-500 bg-blue-50'
                        : 'border-gray-300 hover:border-blue-300'
                    }`}
                  >
                    <div className="text-2xl mb-2">{method.icon}</div>
                    <div className="font-medium">{method.name}</div>
                    <div className="text-sm text-gray-500">{method.description}</div>
                  </button>
                ))}
              </div>
            </div>
            
            {/* Payment Form */}
            <div className="bg-white p-6 border rounded-lg">
              <h2 className="text-xl font-semibold mb-4">
                {selectedMethod === 'card' && 'Card Payment Details'}
                {selectedMethod === 'paypal' && 'PayPal Payment'}
                {selectedMethod === 'bank' && 'Bank Transfer Details'}
                {selectedMethod === 'crypto' && 'Cryptocurrency Payment'}
                {selectedMethod === 'mobile' && 'Mobile Money Payment'}
              </h2>
              
              {selectedMethod === 'card' && (
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium mb-2">Card Number</label>
                    <input
                      type="text"
                      name="number"
                      value={cardDetails.number}
                      onChange={handleCardInputChange}
                      placeholder="1234 5678 9012 3456"
                      className="w-full p-3 border rounded-lg"
                      maxLength="19"
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium mb-2">Expiry Date</label>
                      <input
                        type="text"
                        name="expiry"
                        value={cardDetails.expiry}
                        onChange={handleCardInputChange}
                        placeholder="MM/YY"
                        className="w-full p-3 border rounded-lg"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium mb-2">CVV</label>
                      <input
                        type="text"
                        name="cvv"
                        value={cardDetails.cvv}
                        onChange={handleCardInputChange}
                        placeholder="123"
                        className="w-full p-3 border rounded-lg"
                        maxLength="4"
                      />
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-2">Cardholder Name</label>
                    <input
                      type="text"
                      name="name"
                      value={cardDetails.name}
                      onChange={handleCardInputChange}
                      placeholder="John Doe"
                      className="w-full p-3 border rounded-lg"
                    />
                  </div>
                </div>
              )}
              
              {selectedMethod === 'paypal' && (
                <div className="text-center py-8">
                  <div className="text-4xl mb-4">üì±</div>
                  <p className="text-gray-600 mb-4">You will be redirected to PayPal to complete your payment</p>
                  <button className="bg-yellow-400 text-black px-8 py-3 rounded-lg font-medium">
                    Continue with PayPal
                  </button>
                </div>
              )}
              
              {selectedMethod === 'crypto' && (
                <div className="text-center py-8">
                  <div className="text-4xl mb-4">‚Çø</div>
                  <p className="text-gray-600 mb-4">Pay with Bitcoin, Ethereum, or other cryptocurrencies</p>
                  <div className="bg-gray-100 p-4 rounded-lg mb-4">
                    <p className="font-mono text-sm">1A1zP1eP5QGefi2DMPTfTL5SLmv7DivfNa</p>
                  </div>
                  <p className="text-sm text-gray-500">Send exact amount to this wallet address</p>
                </div>
              )}
              
              <div className="mt-6">
                <button
                  onClick={handleProceedToPayment}
                  disabled={isProcessing}
                  className="w-full py-4 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:bg-gray-400 transition-colors"
                >
                  {isProcessing ? 'Processing Payment...' : `Pay ¬£${(totalAmount + 5.99 + (totalAmount * 0.2)).toFixed(2)}`}
                </button>
              </div>
              
              <div className="mt-6 p-4 bg-blue-50 rounded-lg">
                <h3 className="font-semibold mb-2">üí≥ Payment Security</h3>
                <p className="text-sm text-gray-600">
                  All payments are encrypted and secure. Card payments use 3D Secure authentication.
                  We never store your card details.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PaymentMethodsPage;
